INSERT INTO `ape_article` VALUES ('1730047963766239234', '1729771050996895745', '2023-12-03 23:10:45', 'SpringBoot实现文件批量打包下载', '/img/1697436716646531073.jpg', '实现将指定的多个文件打包成一个压缩文件下载。', '<h3>1. 引入pom依赖</h3><pre class=\"ql-syntax\" spellcheck=\"false\">&lt;dependencies&gt;\n&nbsp;&nbsp;&lt;!-- Spring Web --&gt;\n&nbsp;&nbsp;&lt;dependency&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;org.springframework.boot&lt;/groupId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;spring-boot-starter-web&lt;/artifactId&gt;\n&nbsp;&nbsp;&lt;/dependency&gt;\n&nbsp;&nbsp;&lt;!-- Apache Commons IO --&gt;\n&nbsp;&nbsp;&lt;dependency&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;commons-io&lt;/groupId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;commons-io&lt;/artifactId&gt;\n&nbsp;&nbsp;&lt;/dependency&gt;\n&nbsp;&nbsp;&lt;!-- Apache Commons Compress --&gt;\n&nbsp;&nbsp;&lt;dependency&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;org.apache.commons&lt;/groupId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;commons-compress&lt;/artifactId&gt;\n&nbsp;&nbsp;&lt;/dependency&gt;\n&lt;/dependencies&gt;\n</pre><h3>2. 编写控制器</h3><pre class=\"ql-syntax\" spellcheck=\"false\">import org.apache.commons.compress.archivers.ArchiveEntry;\nimport org.apache.commons.compress.archivers.zip.ZipArchiveEntry;\nimport org.apache.commons.compress.archivers.zip.ZipArchiveOutputStream;\nimport org.springframework.http.HttpHeaders;\nimport org.springframework.http.HttpStatus;\nimport org.springframework.http.ResponseEntity;\nimport org.springframework.web.bind.annotation.GetMapping;\nimport org.springframework.web.bind.annotation.RequestParam;\nimport org.springframework.web.bind.annotation.RestController;\n&nbsp;\nimport java.io.File;\nimport java.io.FileInputStream;\nimport java.io.FileOutputStream;\nimport java.io.IOException;\nimport java.util.List;\n&nbsp;\n@RestController\npublic class FileDownloadController {\n&nbsp;\n&nbsp;&nbsp;@GetMapping(\"/download\")\n&nbsp;&nbsp;public ResponseEntity&lt;byte[]&gt; downloadFiles(@RequestParam List&lt;String&gt; fileNames) throws IOException {\n&nbsp;&nbsp;&nbsp;&nbsp;// 创建临时压缩文件\n&nbsp;&nbsp;&nbsp;&nbsp;File zipFile = File.createTempFile(\"download\", \".zip\");\n&nbsp;&nbsp;&nbsp;&nbsp;try (FileOutputStream fos = new FileOutputStream(zipFile);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ZipArchiveOutputStream zos = new ZipArchiveOutputStream(fos)) {\n&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for (String fileName : fileNames) {\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;File fileToZip = new File(fileName);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;FileInputStream fis = new FileInputStream(fileToZip);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ArchiveEntry entry = new ZipArchiveEntry(fileToZip.getName());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;zos.putArchiveEntry(entry);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;byte[] buffer = new byte[1024];\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int len;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;while ((len = fis.read(buffer)) &gt; 0) {\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;zos.write(buffer, 0, len);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fis.close();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;zos.closeArchiveEntry();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;zos.finish();\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;// 设置HTTP响应头\n&nbsp;&nbsp;&nbsp;&nbsp;HttpHeaders headers = new HttpHeaders();\n&nbsp;&nbsp;&nbsp;&nbsp;headers.add(\"Content-Disposition\", \"attachment; filename=download.zip\");\n&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;// 读取压缩文件内容并返回给客户端\n&nbsp;&nbsp;&nbsp;&nbsp;byte[] fileData = org.apache.commons.io.FileUtils.readFileToByteArray(zipFile);\n&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;return ResponseEntity\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;.status(HttpStatus.OK)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;.headers(headers)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;.body(fileData);\n&nbsp;&nbsp;}\n}\n</pre><h3>3. 发送GET请求</h3>', 0, '', '白易', '2023-11-30 10:15:52', '超级管理员', 'SpringBoot深入浅出大师课', 'c864ceb6e258c75d183ba54a2033bd77');
INSERT INTO `ape_article` VALUES ('1730048774575534082', '1729769998658605057', '2023-11-30 11:29:30', 'uniapp：九宫格，简单易上手，拿来即用', '/img/1697436716646531073.jpg', 'uniapp：九宫格，简单易上手，拿来即用', '<pre class=\"ql-syntax\" spellcheck=\"false\">&lt;template&gt;\n	&lt;view class=\"index\"&gt;\n		&lt;u-navbar title=\"\" :is-back=\"false\" :border-bottom=\"false\" title-color=\"#333\" :background=\"{background:\'\'}\"&gt;\n			&lt;view class=\"page_navbar_warp\"&gt;\n				&lt;image src=\"../../static/icon/0.png\" mode=\"\" class=\"page_navbar_commonImg\" @click=\"$go(1,1)\"&gt;&lt;/image&gt;\n			&lt;/view&gt;\n		&lt;/u-navbar&gt;\n \n		&lt;div class=\"gameBox\"&gt;\n			&lt;view class=\"top\"&gt;\n				&lt;view class=\"user\"&gt;\n					&lt;view class=\"left\"&gt;\n						&lt;image src=\"../../static/logo.png\" mode=\"\"&gt;&lt;/image&gt;\n						&lt;view class=\"text\"&gt;某某某店铺&lt;/view&gt;\n					&lt;/view&gt;\n					&lt;view class=\"right\"&gt;进入店铺&lt;/view&gt;\n				&lt;/view&gt;\n				&lt;view class=\"num\"&gt;剩余抽奖次数：1&lt;/view&gt;\n			&lt;/view&gt;\n			&lt;view class=\"content\"&gt;\n				&lt;image src=\"../../static/img/196.png\" mode=\"\" class=\"start\" @click=\"move\"&gt;&lt;/image&gt;\n				&lt;view class=\"ul\"&gt;\n				&nbsp;&nbsp;&lt;view v-for=\"(item,i) in list\" :key=\"i\" class=\"li\" :class=\"[\'item\' + (i+1), {\'active\': index == i}]\"&gt;\n				&nbsp;&nbsp;&nbsp;&nbsp;&lt;image src=\"../../static/img/199.png\" mode=\"\"&gt;&lt;/image&gt;\n				&nbsp;&nbsp;&nbsp;&nbsp;&lt;view class=\"text1\"&gt;+{{item.number}}{{item.prize_name}}&lt;/view&gt;\n				&nbsp;&nbsp;&lt;/view&gt;\n				&lt;/view&gt;\n			&lt;/view&gt;\n			&lt;view class=\"bom\"&gt;\n				&lt;image src=\"../../static/img/193.png\" mode=\"\"&gt;&lt;/image&gt;\n				&lt;image src=\"../../static/img/194.png\" mode=\"\"&gt;&lt;/image&gt;\n			&lt;/view&gt;\n		&lt;/div&gt;\n		&lt;view class=\"list\"&gt;\n			&lt;view class=\"box\"&gt;\n				&lt;view class=\"title\"&gt;中奖名单&lt;/view&gt;\n				&lt;view class=\"item\" v-for=\"(item,index) in 13\" :key=\"index\"&gt;\n					&lt;view class=\"text\"&gt;4-13 13:21&lt;/view&gt;\n					&lt;view class=\"text\"&gt;用户昵称&lt;/view&gt;\n					&lt;view class=\"text\"&gt;华为手机*1&lt;/view&gt;\n				&lt;/view&gt;\n			&lt;/view&gt;\n		&lt;/view&gt;\n\n	&lt;/view&gt;\n&lt;/template&gt;\n\n&lt;script&gt;\n	export default {\n		data() {\n		&nbsp;&nbsp;return {\n		&nbsp;&nbsp;&nbsp;&nbsp;list: [],//奖品列表\n		&nbsp;&nbsp;&nbsp;&nbsp;index: 0, // 当前转动到哪个位置，第一次起点位置0,对应页面item1位置\n		&nbsp;&nbsp;&nbsp;&nbsp;count: 8, // 总共有多少个位置\n		&nbsp;&nbsp;&nbsp;&nbsp;times: 0, // 转动跑格子次数,\n		&nbsp;&nbsp;&nbsp;&nbsp;cycle: 100, // 转动基本次数：即至少需要转动多少次再进入抽奖环节\n		&nbsp;&nbsp;&nbsp;&nbsp;speed: 40, // 初始转动速度\n		&nbsp;&nbsp;&nbsp;&nbsp;timer: 0, // 转动定时器\n		&nbsp;&nbsp;&nbsp;&nbsp;prize: 0, // 中奖位置，接口返回\n		&nbsp;&nbsp;&nbsp;&nbsp;number_of_draws:0,//限制每天抽奖次数，接口返回\n		&nbsp;&nbsp;&nbsp;&nbsp;prize_data: {//中奖信息\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;id:Number,//奖品ID\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;name:\'\',//奖品名称\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;number:Number,//奖品数量\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;image:\'\',//奖品图片\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;type:Number,// 奖品类型\n		&nbsp;&nbsp;&nbsp;&nbsp;},\n		&nbsp;&nbsp;}\n		},\n		mounted() {\n		&nbsp;&nbsp;//获取奖品列表\n			this.list = [\n				{\n					id:1,\n					number:1,\n					prize_name:\'乐豆\'\n				},\n				{\n					id:2,\n					number:2,\n					prize_name:\'乐豆\'\n				},\n				{\n					id:3,\n					number:3,\n					prize_name:\'乐豆\'\n				},\n				{\n					id:4,\n					number:4,\n					prize_name:\'乐豆\'\n				},\n				{\n					id:5,\n					number:5,\n					prize_name:\'乐豆\'\n				},\n				{\n					id:6,\n					number:6,\n					prize_name:\'乐豆\'\n				},\n				{\n					id:7,\n					number:7,\n					prize_name:\'乐豆\'\n				},\n				{\n					id:8,\n					number:8,\n					prize_name:\'乐豆\'\n				}\n			]; // 奖品列表数据\n			this.number_of_draws = 2; // 该用户剩余抽奖次数\n		},\n		methods: {\n		&nbsp;&nbsp;//点击开始抽奖\n		&nbsp;&nbsp;move() {\n		&nbsp;&nbsp;&nbsp;&nbsp;if( this.number_of_draws == 0){\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.$toast(\'今日抽奖次数已用完,明天再来吧\');\n		&nbsp;&nbsp;&nbsp;&nbsp;}else if(this.times != 0){\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.$toast(\'正在抽奖中，请勿重复点击\')\n		&nbsp;&nbsp;&nbsp;&nbsp;} else{\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.number_of_draws--;//抽奖开始，次数-1\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.speed = 40;//每次抽奖速度初始化为200\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.prize_data = 1;//已经拿到中奖信息，页面展示，等抽奖结束后，将弹窗弹出\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.prize = 1;//中奖位置赋值，跑马灯最终停留位置，这里实际位置需要-1\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.startRoll();//执行抽奖\n		&nbsp;&nbsp;&nbsp;&nbsp;}\n		&nbsp;&nbsp;},\n		&nbsp;&nbsp;// 开始转动\n		&nbsp;&nbsp;startRoll() {\n		&nbsp;&nbsp;&nbsp;&nbsp;this.times += 1; // 转动次数\n		&nbsp;&nbsp;&nbsp;&nbsp;this.oneRoll(); // 转动过程调用的每一次转动方法，这里是第一次调用初始化\n		&nbsp;&nbsp;&nbsp;&nbsp;this.usePrize();\n		&nbsp;&nbsp;},\n		&nbsp;&nbsp;&nbsp;\n		&nbsp;&nbsp;// 每一次转动\n		&nbsp;&nbsp;oneRoll() {\n		&nbsp;&nbsp;&nbsp;&nbsp;let index = this.index; // 当前转动到哪个位置\n		&nbsp;&nbsp;&nbsp;&nbsp;const count = 8; // 总共有多少个位置\n		&nbsp;&nbsp;&nbsp;&nbsp;index += 1;\n		&nbsp;&nbsp;&nbsp;&nbsp;if (index &gt; count - 1) {\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;index = 0;\n		&nbsp;&nbsp;&nbsp;&nbsp;}\n		&nbsp;&nbsp;&nbsp;&nbsp;this.index = index;\n		&nbsp;&nbsp;},\n \n		&nbsp;&nbsp;usePrize() {\n		&nbsp;&nbsp;&nbsp;&nbsp;// 如果当前转动次数达到要求 &amp;&amp; 目前转到的位置是中奖位置\n		&nbsp;&nbsp;&nbsp;&nbsp;if (this.times &gt; this.cycle-20 &amp;&amp; this.prize === this.index) {\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;clearTimeout(this.timer); // 清除转动定时器\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.times = 0; // 转动跑格子次数初始化为0，可以开始下次抽奖\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(this.prize_data.type == 0){\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(\'未中奖，谢谢参与\');//未中奖提示弹出，\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else{\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(\'中奖啦\');//中奖弹出提示\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n		&nbsp;&nbsp;&nbsp;&nbsp;} else {\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(this.times&gt;60){\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.speed += 20; // 抽奖即将结束，放慢转动速度\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.timer = setTimeout(this.startRoll, this.speed);//开始转动\n		&nbsp;&nbsp;&nbsp;&nbsp;}\n		&nbsp;&nbsp;},\n		},\n	}\n&lt;/script&gt;\n\n&lt;style scoped lang=\"scss\"&gt;\n	.index{\n		min-height: 100vh;\n		background: url(\'../../static/img/190.png\')no-repeat left top;\n		background-size: 750rpx 1916rpx;\n		.gameBox {\n			width: 750rpx;\n			height: 1066rpx;\n			background: url(\'../../static/img/191.png\')no-repeat left top;\n			background-size: 750rpx 1066rpx;\n			margin-top: 300rpx;\n			.top{\n				height: 280rpx;\n				.user{\n					display: flex;\n					align-items: center;\n					justify-content: space-between;\n					padding: 85rpx 50rpx 67rpx;\n					.left{\n						display: flex;\n						align-items: center;\n						uni-image{\n							width: 68rpx;\n							height: 68rpx;\n							margin-right: 20rpx;\n							border-radius: 50%;\n						}\n						.text{\n							font-size: 30rpx;\n							font-weight: bold;\n							color: #6E160A;\n						}\n					}\n					.right{\n						padding-right: 20rpx;\n						font-size: 26rpx;\n						font-weight: bold;color: #C52722;\n						background: url(\'../../static/img/198.png\')no-repeat right center;\n						background-size: 16rpx 16rpx;\n					}\n				}\n				.num{\n					text-align: center;\n					font-size: 26rpx;\n					font-weight: bold;\n					color: #FDFBB9;\n				}\n			}\n			.content{\n				position: relative;\n				display: flex;\n				align-items: center;\n				justify-content: center;\n				width: 570rpx;\n				height: 570rpx;\n				margin: auto;\n				.start {\n					position: absolute;\n					width: 164rpx;\n					height: 164rpx;\n					left: 210rpx;top: 200rpx;\n				}\n				.ul {\n					.li {\n						position: absolute;\n						width: 164rpx;\n						height: 164rpx;\n						background: url(\'../../static/img/195.png\')no-repeat center;\n						background-size: 164rpx 164rpx;\n						border-radius: 8px;\n						padding-top: 20rpx;\n						uni-image{\n							display: block;\n							width: 80rpx;\n							height: 80rpx;\n							margin: 0 auto 10rpx;\n						}\n						.text1 {\n							text-align: center;\n							font-size: 20rpx;\n							color: #B53319;\n						}\n					}\n					.item1 {left: 25rpx;top: 20rpx;}\n					.item2 {left: 210rpx;top: 20rpx;}\n					.item3 {left: 390rpx;top: 20rpx;}\n					.item4 {left: 390rpx;top: 200rpx;}\n					.item5 {left: 390rpx;top: 380rpx;}\n					.item6 {left: 210rpx;top: 380rpx;}\n					.item7 {left: 25rpx;top: 380rpx;}\n					.item8 {left: 25rpx;top: 200rpx;}\n					.active {background: #FFF2B1;}\n				}\n			}\n			.bom{\n				display: flex;\n				align-items: center;\n				justify-content: center;\n				height: 220rpx;\n				uni-image{\n					width: 262rpx;\n					height: 104rpx;\n					margin: 0 5rpx;\n				}\n			}\n		}\n		.list{\n			width: 750rpx;\n			height: 544rpx;\n			background: url(\'../../static/img/192.png\')no-repeat left top;\n			background-size: 750rpx 544rpx;\n			overflow: hidden;\n			.box{\n				padding-top: 70rpx;\n				margin-top: 80rpx;\n				height: 400rpx;\n				overflow-y: scroll;\n				.title{\n					text-align: center;\n					font-size: 32rpx;\n					font-weight: bold;\n					color: #FEE3BD;\n					margin-bottom: 30rpx;\n				}\n				.item{\n					width: 690rpx;\n					display: flex;\n					align-items: center;\n					justify-content: space-between;\n					margin: 0 auto 20rpx;\n					.text{\n						text-align: center;\n						width: 33.33%;\n						font-size: 26rpx;\n						color: #FFD9A5;\n					}\n				}\n			}\n		}\n	}\n&lt;/style&gt;\n</pre><p><br></p>', 0, '', '白易', '2023-11-30 10:19:05', '超级管理员', 'UniApp项目实战', 'c864ceb6e258c75d183ba54a2033bd77');
INSERT INTO `ape_article` VALUES ('1730049091539087362', '1729769998658605057', '2023-11-30 11:29:31', 'uniapp上架app store详细攻略', '/img/1697436716646531073.jpg', 'uniapp开发多端应用，打包ios应用后，会生成一个ipa后缀的文件。这个文件无法直接安装在iphone上，需要将这个ipa文件上架app store后，才能通过app store来安装。\n\n但是苹果官方提供的上传工具xcode只能安装在mac电脑上，windows上并没有windows版的上传工具。因此我们这篇文章将教会大家如何通过windows电脑来上架。', '<h3>一、登录苹果开发者网站</h3><p>进入app store connect下的app模块，如下图，查看app有没有创建，假如还没有创建app，则先创建app。要注意创建app的时候，套装ID必须选择跟我们创建Identifies时填写的appId一致的包名，别选错了，选错了就是别的APP。</p><h3>二、创建好APP</h3><p>点击APP名称进入上架资料填写页面，填写好各项资料。然后你会看到其中有一项，需要提供APP在各种分辨率(各种型号)的iphone/ipad设备下的截图，假如我们没有这么多种型号的设备来截图怎么办？你可以使用<a href=\"https://www.applicationloader.net/\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(78, 161, 219);\">appuploder</a>的生成截图功能来截图：</p><p><span style=\"color: rgb(77, 77, 77);\">三、其中它还会要求我们上传一个构建版本，但是它提供的工具xcode无法安装在windows电脑，因此我们这里还是用第二步提到的</span><a href=\"https://www.applicationloader.net/\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(78, 161, 219);\">appuploder</a><span style=\"color: rgb(77, 77, 77);\">来上传。</span></p><p>四、进入<a href=\"https://www.applicationloader.net/\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(78, 161, 219);\">appuploder</a>上传app store的控制台，点击上传新文件即可</p><p><span style=\"color: rgb(77, 77, 77);\">五、然后它会要求我们提供苹果开发者账号的账号名，和上传专用密码，注意这里不是填登录密码哦，是填上传专用密码，在密码输入框下面有专用密码的设置教程。</span></p><p><span style=\"color: rgb(77, 77, 77);\"><span class=\"ql-cursor\">﻿</span>六、上传成功后，大概过30分钟，即可在app store里见到这构建版本，可供选择。然后继续填写其他文字性的资料，然后就可以提交上架进入人工审核了。人工审核完即可在app store里见到。</span></p>', 0, '', '白易', '2023-11-30 10:20:21', '超级管理员', 'UniApp项目实战', 'c864ceb6e258c75d183ba54a2033bd77');
INSERT INTO `ape_article` VALUES ('1730049650568507394', '1729751427215790082', '2023-11-30 11:29:32', '什么是线程（Thread）？进程和线程有什么区别？', '/img/1697436716646531073.jpg', '什么是线程（Thread）？进程和线程有什么区别？', '<p>一、线程（Thread）的概念</p><p>线程是操作系统中实现并发编程的一种基本结构。</p><p>线程可以被看作是运行在操作系统中的一个独立的工作单元，⼀个线程就是⼀个 \"执行流\"。</p><p>每个线程之间都可以按照顺序执行自己的代码。多个线程之间 \"同时\" 执行着多份代码。</p><p>线程的创建和执行由操作系统负责管理，程序员只需要提供线程执行的任务。</p><p>一个进程中可以有多个线程。每个线程也是由一个进程控制块（PCB）进行描述的。</p><p>二、线程存在的意义</p><p>2.1 并发编程</p><p>线程可以提高程序的并发性能，充分利用多核CPU的资源。</p><p>特别是在处理I/O密集型任务时，有些场景需要等待IO，在等待IO时，可以将系统资源调度给其他任务使用。</p><p>当程序需要执行多个任务时，线程可以同时处理这些任务，从而提高程序的执行效率。</p><p>2.2 比进程更“轻量”</p><p>进程也可以进行并发编程，但线程比进程更轻量，线程在创建、调度和销毁上，都要比进程快。</p><p>所以线程可以减轻CPU的负担，提高程序的响应速度。</p><p>线程保持了独立调度执行，支持并发的同时，省去了“分配资源”和“释放资源”带来的额外开销。这意味着只有在第一个线程，也就是进程创建时才需要申请系统资源，后续线程的创建则不再需要重新申请系统资源了。</p><p>三、使用线程时应该注意</p><p>注意以下几点	说明</p><p>线程并非越多越好	线程数量太多时，会加剧线程间对有限的CPU资源的竞争，这增加了资源调度的开销，降低了执行效率。</p><p>资源共享存在副作用	进程和进程之间不会互相影响，但如果同一个进程中的某一个线程抛出异常，这个进程中的其他线程也会受到影响，这就可能导致整个进程异常终止。</p><p>线程安全问题	线程之间可能互相干扰冲突，导致代码出现逻辑错误。线程安全问题也是并发编程的重点和难点。</p><p>四、进程和线程的区别</p><p>（1）进程包含线程。每一个进程至少有一个线程，这个线程被称为主线程。</p><p>（2）进程和进程之间不共享内存空间，每个进程都有自己的资源。同一个进程的线程之间共享同一分资源，如内存空间、文件描述符表等，每个线程都是一个独立的执行流，单独参加到CPU的调度中。</p><p>（3）进程是系统分配资源的最小单位，线程是系统调度的最小单位。</p><p>（4）一个进程发生异常一般不会影响到其他进程。但是一个线程出现异常，则可能导致同进程内的其他线程也发生异常，最终可能导致包含这个线程的进程也出现异常。</p><p>五、Java中的线程和操作系统中的线程是不同的概念</p><p>操作系统中的线程	线程是操作系统中的概念。操作系统内核实现了线程机制，并对用户岑提供了供用户使用的线程相关API。</p><p>Java中的线程	Java标准库中的Thread类是对操作系统提供的API进行了进一步的抽象和封装。</p><p>六、多线程编程</p><p>多线程程序和非多线程程序的区别和优势在于，多线程每个线程都是一个独立的执行流，多个线程可以并发执行，增加了程序运行速度，提高了程序的运行效率。</p><p>许多语言都是支持多进程并发编程，也支持多线程并发编程。但是，在Java中，更鼓励多线程并发编程。</p><p>因为在Java标准库中，很多多进程编程的相关API都没有提供，而多线程编程的API都有封装提供。</p><p>更重要的是，在Java中每启动一个进程，就要启动一次Java虚拟机，这加大了多进程编程的系统开销。</p><p>因此在Java中，更推荐多线程并发编程。</p><p><br></p>', 0, '', '白易', '2023-11-30 10:22:34', '超级管理员', 'Java零基础入门', 'c864ceb6e258c75d183ba54a2033bd77');
INSERT INTO `ape_article` VALUES ('1730049947718168578', '1729751427215790082', '2023-11-30 11:29:33', '【JavaEE初阶】认识线程、创建线程', '/img/1697436716646531073.jpg', '一个线程就是一个 \"执行流\". 每个线程之间都可以按照顺序执行自己的代码. 多个线程之间 \"同时\" 执行着多份代码.', '<p>1.1 概念</p><p>1) 线程是什么</p><p>一个线程就是一个 \"执行流\". 每个线程之间都可以按照顺序执行自己的代码. 多个线程之间 \"同时\" 执行着多份代码.</p><p>举例：</p><p><br></p><p>还是回到我们之前的银⾏的例⼦中。之前我们主要描述的是个⼈业务，即⼀个⼈完全处理⾃⼰的业务。我们进⼀步设想如下场景：</p><p>⼀家公司要去银⾏办理业务，既要进⾏财务转账，⼜要进⾏福利发放，还得进⾏缴社保。</p><p>如果只有张三⼀个会计就会忙不过来，耗费的时间特别⻓。为了让业务更快的办理好，张三⼜找来两位同事李四、王五⼀起来帮助他，三个⼈分别负责⼀个事情，分别申请⼀个号码进⾏排队，⾃此就有了三个执⾏流共同完成任务，但本质上他们都是为了办理⼀家公司的业务。 此时，我们就把这种情况称为多线程，将⼀个⼤任务分解成不同⼩任务，交给不同执⾏流就分别排队执⾏。其中李四、王五都是张三叫来的，所以张三⼀般被称为主线程（Main Thread）。</p><p>2) 为何要有线程&nbsp;&nbsp;</p><p>首先，我们来说一下进程。在多任务操作系统中，希望系统能够同时运行多个程序，这就引入了进程。如果是单任务的操作系统，就完全不涉及进程，也不需要管理（进程），更不需要调度。本质上说，进程是解决”并发编程“问题的，事实上，进程也可以很好地解决并发编程这样的问题。</p><p><br></p><p>但是在一些特定的环境下，进程的表现不尽人意，比如，有些场景下，需要频繁的创建和销毁进程，举例，最早的web开发，是使用C语言来编写的服务器程序（基于一种CGI这样的技术，其基于多进程的编程模式），服务器同一时刻会收到很多请求，针对每个请求，都会创建出一个进程，给这个请求提供一定的服务，返回对应的响应；一旦这个请求处理完了，此时这个进程就要销毁了。如果请求很多，就意味着服务器要不停地创建进程、销毁进程，此时使用多进程编程，系统的开销就会很大（开销主要体现在资源的申请和释放上）。&nbsp;</p><p><br></p><p>⾸先, \"并发编程\" 成为 \"刚需\"</p><p><br></p><p>单核 CPU 的发展遇到了瓶颈. 要想提⾼算力, 就需要多核 CPU，而并发编程能更充分利⽤多核 CPU资源</p><p>有些任务场景需要 \"等待 IO\", 为了让等待 IO 的时间能够去做⼀些其他的⼯作, 也需要⽤到并发编程</p><p>其次, 虽然多进程也能实现 并发编程, 但是线程比进程更轻量.</p><p>创建线程⽐创建进程更快.</p><p>销毁线程⽐销毁进程更快.</p><p>调度线程⽐调度进程更快.</p><p>多线程并发编程，效率更高，尤其是对于java进程，是要启动java虚拟机的，启动java虚拟机开销是更大的，搞多个java进程，就要多个java虚拟机。所以，java中不太去鼓励多进程编程。</p><p><br></p><p>最后, 线程虽然比进程轻量, 但是人们还不满足, 于是又有了 \"线程池\"(ThreadPool) 和 \"协程\"</p><p>(Coroutine)</p><p>3) 进程和线程的区别</p><p>进程是包含线程的. 每个进程⾄少有⼀个线程存在，即主线程。</p><p>进程和进程之间不共享内存空间. 同⼀个进程的线程之间共享同⼀个内存空间.</p><p>⽐如之前的多进程例⼦中，每个客户来银⾏办理各⾃的业务，但他们之间的票据肯定是不想让别⼈知道的，否则钱不就被其他⼈取⾛了么。⽽上⾯我们的公司业务中，张三、李四、王五虽然是不同的执⾏流，但因为办理的都是⼀家公司的业务，所以票据是共享着的。这个就是多线程和多进程的最⼤区别。</p><p>进程是系统分配资源的最小单位，线程是系统调度的最小单位。</p><p>⼀个进程挂了⼀般不会影响到其他进程. 但是⼀个线程挂了, 可能把同进程内的其他线程⼀起带⾛(整个进程崩溃).</p><p><br></p>', 0, '', '白易', '2023-11-30 10:23:45', '超级管理员', 'Java零基础入门', 'c864ceb6e258c75d183ba54a2033bd77');
INSERT INTO `ape_article` VALUES ('1730051124686008322', '1729771050996895745', '2023-11-30 11:29:34', 'Spring Boot 升级3.x 指南', '/img/1697436716646531073.jpg', '动手前一定要调研项目中使用的组件，某些组件是没法升级的。比如ElasticSearch，驱动版本和ElasticSearch server版本要一致，升级了就会报错。其它常见组件的有Nacos，Kafka，Mysql，RocketMQ，需要调研是否兼容低版本。\n如果之前使用了Zuul 1.x作为Gateway，Zuul 2.x不开源并且难以升级，建议升级到Spring Cloud Gateway\n如果使用了Zuul1.x的作为Proxy嵌入服务中，有两个思路，一个是使用Filter+HttpClient手动写转发代码，第二个思路是调研调用接口，使用Feign做转发\nSpring Boot 3.x 需要JDK17，建议使用OpenJDK17，Oracle的JDK17可能存在授权问题', '<h3>1. 升级思路</h3><p>先创建一个parent项目，打包类型为pom，继承自spring boot的parent项目</p><pre class=\"ql-syntax\" spellcheck=\"false\">&lt;parent&gt;\n&nbsp;&nbsp;&lt;groupId&gt;org.springframework.boot&lt;/groupId&gt;\n&nbsp;&nbsp;&lt;artifactId&gt;spring-boot-starter-parent&lt;/artifactId&gt;\n&nbsp;&nbsp;&lt;version&gt;3.x&lt;/version&gt;\n&lt;/parent&gt;\n</pre><p><span style=\"color: rgb(77, 77, 77);\">然后把版本集中放在这个pom里面，示例如下</span></p><pre class=\"ql-syntax\" spellcheck=\"false\">&lt;properties&gt;\n&nbsp;&nbsp;&lt;!-- 建议添加全局变量 java.version，maven.compiler.source， maven.compiler.target--&gt;\n&nbsp;&nbsp;&lt;java.version&gt;17&lt;/java.version&gt;\n&nbsp;&nbsp;&lt;maven.compiler.source&gt;17&lt;/maven.compiler.source&gt;\n&nbsp;&nbsp;&lt;maven.compiler.target&gt;17&lt;/maven.compiler.target&gt;\n&nbsp;&nbsp;&lt;spring-cloud-dependencies.version&gt;2022.0.4&lt;/spring-cloud-dependencies.version&gt;\n&nbsp;&nbsp;&lt;spring-cloud-starter-netflix.version&gt;2.2.10.RELEASE&lt;/spring-cloud-starter-netflix.version&gt;\n&lt;/properties&gt;\n</pre><p><span style=\"color: rgb(77, 77, 77);\">然后添加</span><code style=\"color: rgb(199, 37, 78); background-color: rgb(249, 242, 244);\">dependencyManagement</code><span style=\"color: rgb(77, 77, 77);\">节点，示例如下：</span></p><pre class=\"ql-syntax\" spellcheck=\"false\">&lt;dependencyManagement&gt;&nbsp;&nbsp;\n&nbsp;&nbsp;&lt;dependencies&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;dependency&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;org.springframework.cloud&lt;/groupId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;spring-cloud-dependencies&lt;/artifactId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;version&gt;${spring-cloud-dependencies.version}&lt;/version&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;type&gt;pom&lt;/type&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;scope&gt;import&lt;/scope&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/dependency&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;dependency&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;org.springframework.cloud&lt;/groupId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;spring-cloud-starter-netflix-ribbon&lt;/artifactId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;version&gt;${spring-cloud-starter-netflix.version}&lt;/version&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;exclusions&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;exclusion&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;jsr311-api&lt;/artifactId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;javax.ws.rs&lt;/groupId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/exclusion&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;exclusion&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;jackson-annotations&lt;/artifactId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;com.fasterxml.jackson.core&lt;/groupId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/exclusion&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;exclusion&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;jackson-core&lt;/artifactId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;com.fasterxml.jackson.core&lt;/groupId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/exclusion&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;exclusion&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;jackson-databind&lt;/artifactId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;com.fasterxml.jackson.core&lt;/groupId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/exclusion&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;exclusion&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;guava&lt;/artifactId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;com.google.guava&lt;/groupId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/exclusion&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/exclusions&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/dependency&gt;\n&nbsp;&nbsp;&lt;dependencies&gt;\n&lt;/dependencyManagement&gt;\n</pre><p><br></p><p><br></p><p><br></p><p><br></p>', 0, '', '白易', '2023-11-30 10:28:26', '超级管理员', 'SpringBoot深入浅出大师课', 'c864ceb6e258c75d183ba54a2033bd77');
INSERT INTO `ape_article` VALUES ('1730064463969431553', '1729751427215790082', '2023-11-30 11:29:36', 'BIO、NIO和AIO的区别', '/img/1697436716646531073.jpg', '同步指的是任务按顺序依次执行，一个任务的执行必须等待前一个任务的完成。\n在同步处理中，任务的调用者需要主动等待任务的完成，并在等待期间阻塞并占用资源。\n在同步模式下，任务的执行顺序是确定的，具有可预测性。', '<p>一、基础知识：&nbsp;</p><p>&nbsp;I/O 模型的简单理解：</p><p>1.BIO（Blocking I/O）：同步阻塞，一个线程处理一个通道上的事件。</p><p><br></p><p>2.NIO（Non-blocking I/O）：同步非阻塞，使用选择器（Selector）来管理多个通道，一个线程可以按某种顺序处理多个通道上的事件(同一时间一个线程只能处理一个通道)。</p><p><br></p><p>3.AIO（Asynchronous I/O）：异步非阻塞，使用操作系统提供的异步I/O机制来管理多个通道，一个线程可以同时处理多个通道上的事件(同一时间一个线程可以处理多个通道)</p><p><br></p><p>&nbsp;同步与异步：</p><p>同步（Synchronous）：</p><p><br></p><p>同步指的是任务按顺序依次执行，一个任务的执行必须等待前一个任务的完成。</p><p>在同步处理中，任务的调用者需要主动等待任务的完成，并在等待期间阻塞并占用资源。</p><p>在同步模式下，任务的执行顺序是确定的，具有可预测性。</p><p>异步（Asynchronous）：</p><p><br></p><p>异步指的是任务的执行不依赖于前一个任务的完成，可以同时执行多个任务，任务的执行结果可能以回调函数或事件的形式返回。</p><p>在异步处理中，任务的调用者不需要主动等待任务的完成，而是继续执行其他任务，真正的处理工作由其他线程、进程或者操作系统来完成。</p><p>在异步模式下，任务的执行顺序不确定，具有不可预测性，但可以提高系统的并发性和响应性。</p><p>二、BIO、NIO和AIO的区别</p><p>I/O 模型：</p><p>BIO：调用线程会在读写操作上阻塞，直到数据完全传输或发生错误。</p><p><br></p><p>NIO：使用选择器轮询通道上的事件，并处理已经就绪的通道，可以处理多个连接，但仍然需要一个线程来处理一个通道的 I/O 操作，一个线程可以对应多个通道(常用组件有Channel和Buffer)。</p><p><br></p><p>AIO：I/O 操作在后台由操作系统进行处理，调用线程可以继续处理其他任务，当 I/O 操作完成时，操作系统会通知应用程序。</p><p><br></p><p>并发支持：</p><p>BIO：每个连接都需要一个独立的线程处理，当并发连接数增加时，线程数量会大量增加，对系统资源消耗较大。</p><p><br></p><p>NIO：使用较少的线程处理多个连接，线程可以处理多个通道的就绪事件。</p><p><br></p><p>假设一个线程可以处理 5 个通道的事件，当有多个通道就绪时，线程会按照某种顺序（例如选择器上注册的顺序）依次处理每个就绪的通道。在处理一个就绪通道的事件时，如果该事件涉及阻塞的操作（如 I/O 操作），那么该线程可能会在这个操作上阻塞，而不会处理其他通道的事件，如果这5个通道中第一个通道上的事件已经被处理，那么在下一个轮询周期时，选择器会重新检查该通道的状态，如果有新的事件进入该通道，那么该通道也会重新进入排队，等待处理。</p><p><br></p><p>AIO：通过回调机制实现异步处理，不需要线程等待 I/O 操作完成，可以处理大量的并发连接而不需要过多的线程。</p><p><br></p><p>编程方式：</p><p>BIO：采用阻塞方式，代码编写简单直观，但对并发连接数有限制。</p><p><br></p><p>NIO：采用事件驱动和选择器的方式，代码编写较复杂，需要处理就绪事件。</p><p><br></p><p>AIO：通过回调机制，在操作完成时触发回调函数进行处理，编程方式相对复杂，但相对于 NIO 更容易实现高并发。</p><p><br></p><p>性能表现：</p><p>BIO：适用于连接数较少且处理逻辑简单的情况，对于大规模并发请求，性能较差。</p><p><br></p><p>NIO：适用于连接数多但交互较简单的情况，适合处理长连接和高并发连接。</p><p><br></p><p>AIO：可以在处理大量简单或者复杂的I/O请求时提供好的性能。</p><p><br></p>', 0, '', '白易', '2023-11-30 11:21:26', '超级管理员', 'Java零基础入门', 'c864ceb6e258c75d183ba54a2033bd77');
INSERT INTO `ape_article` VALUES ('1730065019643408386', '1729771050996895745', '2023-11-30 11:29:36', '在 Spring Boot 中使用 MyBatis-Plus 进行批量操作数据', '/img/1697436716646531073.jpg', 'MyBatis-Plus 是 MyBatis 的增强工具包，提供了许多便捷的功能来简化 MyBatis 的使用。在实际项目中，我们经常需要进行批量的数据操作，例如批量插入、批量更新和批量删除。本文将介绍如何在 Spring Boot 中使用 MyBatis-Plus 进行这些批量操作。', '<h3>1. 准备工作</h3><p>首先，确保你的 Spring Boot 项目中已经添加了 MyBatis-Plus 的依赖。在 Maven 项目中，可以在&nbsp;<code>pom.xml</code>&nbsp;文件中添加以下依赖：</p><pre class=\"ql-syntax\" spellcheck=\"false\">&lt;dependency&gt;\n&nbsp;&nbsp;&lt;groupId&gt;com.baomidou&lt;/groupId&gt;\n&nbsp;&nbsp;&lt;artifactId&gt;mybatis-plus-boot-starter&lt;/artifactId&gt;\n&nbsp;&nbsp;&lt;version&gt;最新版本&lt;/version&gt;\n&lt;/dependency&gt;\n</pre><h3>2. 批量插入数据</h3><pre class=\"ql-syntax\" spellcheck=\"false\">@Service\npublic class UserServiceImpl extends ServiceImpl&lt;UserMapper, User&gt; implements UserService {\n&nbsp;\n&nbsp;&nbsp;public void saveBatchUsers(List&lt;User&gt; userList) {\n&nbsp;&nbsp;&nbsp;&nbsp;saveBatch(userList);\n&nbsp;&nbsp;}\n}\n</pre><p><span style=\"color: rgb(77, 77, 77);\">在上述代码中，</span><code style=\"color: rgb(77, 77, 77);\">saveBatch</code><span style=\"color: rgb(77, 77, 77);\">&nbsp;方法接收一个实体列表作为参数，然后执行批量插入操作。</span><code style=\"color: rgb(77, 77, 77);\">UserMapper</code><span style=\"color: rgb(77, 77, 77);\">&nbsp;是你的&nbsp;</span><a href=\"https://so.csdn.net/so/search?q=MyBatis&amp;spm=1001.2101.3001.7020\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(252, 85, 49);\">MyBatis</a><span style=\"color: rgb(77, 77, 77);\">&nbsp;Mapper 接口，</span><code style=\"color: rgb(77, 77, 77);\">User</code><span style=\"color: rgb(77, 77, 77);\">&nbsp;是你的实体类。</span></p><h3>3. 批量更新数据</h3><pre class=\"ql-syntax\" spellcheck=\"false\">@Service\npublic class UserServiceImpl extends ServiceImpl&lt;UserMapper, User&gt; implements UserService {\n&nbsp;\n&nbsp;&nbsp;public void updateBatchUsers(List&lt;User&gt; userList) {\n&nbsp;&nbsp;&nbsp;&nbsp;updateBatchById(userList);\n&nbsp;&nbsp;}\n}\n</pre><h3>4. 批量删除数据</h3><pre class=\"ql-syntax\" spellcheck=\"false\">@Service\npublic class UserServiceImpl extends ServiceImpl&lt;UserMapper, User&gt; implements UserService {\n&nbsp;\n&nbsp;&nbsp;public void removeUsersByIds(List&lt;Long&gt; userIds) {\n&nbsp;&nbsp;&nbsp;&nbsp;removeByIds(userIds);\n&nbsp;&nbsp;}\n}\n</pre><p>5. 总结</p><p>通过 MyBatis-Plus 提供的 saveBatch、updateBatchById 和 removeByIds 方法，我们可以非常方便地实现在 Spring Boot 项目中的批量操作数据。这些方法简化了 SQL 的编写，提高了开发效率，同时也遵循了 MyBatis-Plus 的约定大于配置的设计理念。</p><p><br></p><p>在实际应用中，根据业务需求和数据量，选择合适的批量操作方法可以有效提高系统性能，减少数据库交互次数。希望本文能帮助你更好地利用 MyBatis-Plus 进行批量操作数据的开发。</p><p><br></p>', 0, '', '白易', '2023-11-30 11:23:38', '超级管理员', 'SpringBoot深入浅出大师课', 'c864ceb6e258c75d183ba54a2033bd77');
INSERT INTO `ape_article` VALUES ('1730065529532362754', '1729767297673674754', '2023-11-30 11:29:37', 'mybatis(mybatis-plus)报invalid bound statement (not found)或者找不到xml文件(各种情况)', '/img/1697436716646531073.jpg', 'mybatis(mybatis-plus)报invalid bound statement (not found)或者找不到xml文件(各种情况)', '<h2>情况1：查看yml文件是否添加mybatis配置</h2><pre class=\"ql-syntax\" spellcheck=\"false\">mybatis-plus:\n&nbsp;# Mapper.xml 文件位置 Maven 多模块项目的扫描路径需以 classpath*: 开头\n&nbsp;# 实现接口绑定\n&nbsp;mapperLocations: classpath*:mybatis/xml/*Mapper.xml\n</pre><h2>情况2：区分使用的的版本是mybatis还是mybstis-plus，yml配置也不同</h2><pre class=\"ql-syntax\" spellcheck=\"false\">#mybatis:\nmybatis-plus:\n&nbsp;# Mapper.xml 文件位置 Maven 多模块项目的扫描路径需以 classpath*: 开头\n&nbsp;# 实现接口绑定\n&nbsp;mapperLocations: classpath*:mybatis/xml/*Mapper.xml\n</pre><h2>情况3：调用mybatis(mybatis-plus)的IService的查询方法看是否也报这个错误，如果也报这个错误就是SqlSessionFactory手动注入了，如下</h2><pre class=\"ql-syntax\" spellcheck=\"false\">&nbsp;&nbsp;@Bean(\"sqlSessionFactory\")\n&nbsp;&nbsp;public SqlSessionFactory sqlSessionFactoryBean(DynamicDataSource dynamicDataSource) throws Exception {\n\n&nbsp;&nbsp;&nbsp;&nbsp;//MybatisSqlSessionFactoryBean sessionFactory = new MybatisSqlSessionFactoryBean();\n&nbsp;&nbsp;&nbsp;&nbsp;SqlSessionFactoryBean sessionFactory = new SqlSessionFactoryBean();\n&nbsp;&nbsp;&nbsp;&nbsp;sessionFactory.setDataSource(dynamicDataSource);\n&nbsp;&nbsp;&nbsp;&nbsp;//sessionFactory.setMapperLocations(new PathMatchingResourcePatternResolver().getResources(\"classpath*:com/example/poi/mapper/**/xml/*Mapper.xml\"));\n&nbsp;&nbsp;&nbsp;&nbsp;return sessionFactory.getObject();\n&nbsp;&nbsp;}\n</pre><h2>如上的方法永远不会调用到mybatis的bean，需要修改成如下才行，同时在yml配置的mybatis都失效了，需要在SqlSessionFactory 设置，如设置读取*Mapper.xml的路径</h2><pre class=\"ql-syntax\" spellcheck=\"false\">@Bean(\"sqlSessionFactory\")\n&nbsp;&nbsp;public SqlSessionFactory sqlSessionFactoryBean(DynamicDataSource dynamicDataSource) throws Exception {\n&nbsp;&nbsp;&nbsp;&nbsp;MybatisSqlSessionFactoryBean sessionFactory = new MybatisSqlSessionFactoryBean();\n&nbsp;&nbsp;&nbsp;&nbsp;sessionFactory.setDataSource(dynamicDataSource);\n&nbsp;&nbsp;&nbsp;&nbsp;/// 创建Configuration对象\n&nbsp;&nbsp;&nbsp;&nbsp;MybatisConfiguration mybatisConfiguration = new MybatisConfiguration();\n&nbsp;&nbsp;&nbsp;&nbsp;mybatisConfiguration.setLogImpl(org.apache.ibatis.logging.stdout.StdOutImpl.class); // 设置日志输出到控制台\n&nbsp;&nbsp;&nbsp;&nbsp;sessionFactory.setConfiguration(mybatisConfiguration);\n&nbsp;&nbsp;&nbsp;&nbsp;sessionFactory.setMapperLocations(new PathMatchingResourcePatternResolver().getResources(\"classpath*:com/example/poi/mapper/**/xml/*Mapper.xml\"));\n&nbsp;&nbsp;&nbsp;&nbsp;return sessionFactory.getObject();\n&nbsp;&nbsp;}\n</pre><p>情况4：使用mybatis-plus必须导入以下依赖(不要使用spring的mybatis，不然和spring本身数据库管理冲突，还有就是注意使用springboot版本和mybatis版本问题，建议都是使用mybatis-plus，不然使用mybatis可能出现sqlFactory找不到等问题)</p><pre class=\"ql-syntax\" spellcheck=\"false\">&nbsp;&nbsp;&nbsp;&nbsp;&lt;dependency&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;com.baomidou&lt;/groupId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;mybatis-plus-boot-starter&lt;/artifactId&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;version&gt;3.5.1&lt;/version&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/dependency&gt;\n</pre><h3>情况5：打开项目的target目录，观察里面是否有对应的××Mapper.xml文件，若没有，则在pom.xml文件中加入如下配置</h3><pre class=\"ql-syntax\" spellcheck=\"false\">	&lt;build&gt;\n		&lt;resources&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;resource&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;directory&gt;src/main/resources&lt;/directory&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;includes&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;include&gt;**/*.properties&lt;/include&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;include&gt;**/*.yml&lt;/include&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;include&gt;**/*.xml&lt;/include&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/includes&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;filtering&gt;true&lt;/filtering&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/resource&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;resource&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;directory&gt;src/main/java&lt;/directory&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;includes&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;include&gt;**/*.properties&lt;/include&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;include&gt;**/*.xml&lt;/include&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/includes&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;filtering&gt;true&lt;/filtering&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/resource&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/resources&gt;\n</pre><p><br></p>', 0, '', '白易', '2023-11-30 11:25:40', '超级管理员', 'Mybatis详细教程入门', 'c864ceb6e258c75d183ba54a2033bd77');
INSERT INTO `ape_article` VALUES ('1730065914896625666', '1729771964436615169', '2023-11-30 11:29:38', 'RabbitMQ消息模型之发布订阅Publish-Subscribe', '/img/1697436716646531073.jpg', '发布订阅模型也称为广播模型，交换机类型需要指定为Fanout，正如从名称中猜到的那样，它是将接收到的所有消息广播到它知道的所有队列中。每个消费者都监听自己的队列，所以同一个消息，会被所有的消费者共同消费。Fanout 这种交换类型并不能给我们带来很大的灵活性，它只能进行无意识的广播。', '<p>在广播模式下，消息发送流程是这样的：</p><p><br></p><p>可以有多个消费者。</p><p>每个消费者有自己的Queue。</p><p>每个队列都要绑定到Exchange。</p><p>生产者发送的消息，只能发送到交换机，交换机来决定要发给哪个队列，生产者无法决定。</p><p>交换机把消息发送给绑定过的所有队列。</p><p>队列的消费者都能拿到消息。实现一条消息被多个消费者消费。</p><h4>创建生产者</h4><pre class=\"ql-syntax\" spellcheck=\"false\">public class MyProducer {\n\n&nbsp;&nbsp;@Test\n&nbsp;&nbsp;public void test() throws Exception {\n&nbsp;&nbsp;&nbsp;&nbsp;// Fanout模式不需要指定队列\n&nbsp;&nbsp;&nbsp;&nbsp;String queue = \"\";\n&nbsp;&nbsp;&nbsp;&nbsp;// 交换机\n&nbsp;&nbsp;&nbsp;&nbsp;String exchange = \"logs\";\n\n&nbsp;&nbsp;&nbsp;&nbsp;// 创建工厂\n&nbsp;&nbsp;&nbsp;&nbsp;ConnectionFactory factory = new ConnectionFactory();\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setVirtualHost(\"/\");\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setHost(\"xuewei.world\");\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setUsername(\"xuewei\");\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setPassword(\"123456\");\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setPort(5672);\n\n&nbsp;&nbsp;&nbsp;&nbsp;// 创建连接和通道\n&nbsp;&nbsp;&nbsp;&nbsp;Connection connection = factory.newConnection();\n&nbsp;&nbsp;&nbsp;&nbsp;Channel channel = connection.createChannel();\n&nbsp;&nbsp;&nbsp;&nbsp;// 声明交换机\n&nbsp;&nbsp;&nbsp;&nbsp;channel.exchangeDeclare(exchange, \"fanout\");\n&nbsp;&nbsp;&nbsp;&nbsp;for (int i = 0; i &lt; 3; i++) {\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;// 发布消息\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;channel.basicPublish(exchange, queue, null, (\"DEBUG LOG -&gt; \" + i).getBytes());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;channel.basicPublish(exchange, queue, null, (\"INFO LOG -&gt; \" + i).getBytes());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;channel.basicPublish(exchange, queue, null, (\"WARN LOG -&gt; \" + i).getBytes());\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;channel.basicPublish(exchange, queue, null, (\"ERROR LOG -&gt; \" + i).getBytes());\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;}\n}\n</pre><h4>创建消费者1</h4><pre class=\"ql-syntax\" spellcheck=\"false\">public class MyConsumer1 {\n\n&nbsp;&nbsp;public static void main(String[] args) throws Exception {\n&nbsp;&nbsp;&nbsp;&nbsp;// 指定交换机\n&nbsp;&nbsp;&nbsp;&nbsp;String exchange = \"logs\";\n\n&nbsp;&nbsp;&nbsp;&nbsp;// 创建工厂\n&nbsp;&nbsp;&nbsp;&nbsp;ConnectionFactory factory = new ConnectionFactory();\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setVirtualHost(\"/\");\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setHost(\"xuewei.world\");\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setUsername(\"xuewei\");\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setPassword(\"123456\");\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setPort(5672);\n\n&nbsp;&nbsp;&nbsp;&nbsp;// 创建连接和通道\n&nbsp;&nbsp;&nbsp;&nbsp;Connection connection = factory.newConnection();\n&nbsp;&nbsp;&nbsp;&nbsp;Channel channel = connection.createChannel();\n&nbsp;&nbsp;&nbsp;&nbsp;//绑定交换机\n&nbsp;&nbsp;&nbsp;&nbsp;channel.exchangeDeclare(\"logs\", \"fanout\");\n&nbsp;&nbsp;&nbsp;&nbsp;//创建临时队列\n&nbsp;&nbsp;&nbsp;&nbsp;String queue = channel.queueDeclare().getQueue();\n&nbsp;&nbsp;&nbsp;&nbsp;//将临时队列绑定exchange\n&nbsp;&nbsp;&nbsp;&nbsp;channel.queueBind(queue, \"logs\", \"\");\n&nbsp;&nbsp;&nbsp;&nbsp;//处理消息\n&nbsp;&nbsp;&nbsp;&nbsp;channel.basicConsume(queue, true, new DefaultConsumer(channel) {\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;public void handleDelivery(String consumerTag, Envelope envelope, AMQP.BasicProperties properties, byte[] body) throws IOException {\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(\"消费者1: \" + new String(body));\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;// TODO 业务处理\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;});\n&nbsp;&nbsp;}\n}\n</pre><h4>创建消费者2</h4><pre class=\"ql-syntax\" spellcheck=\"false\">public class MyConsumer2 {\n\n&nbsp;&nbsp;public static void main(String[] args) throws Exception {\n&nbsp;&nbsp;&nbsp;&nbsp;// 指定交换机\n&nbsp;&nbsp;&nbsp;&nbsp;String exchange = \"logs\";\n\n&nbsp;&nbsp;&nbsp;&nbsp;// 创建工厂\n&nbsp;&nbsp;&nbsp;&nbsp;ConnectionFactory factory = new ConnectionFactory();\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setVirtualHost(\"/\");\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setHost(\"xuewei.world\");\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setUsername(\"xuewei\");\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setPassword(\"123456\");\n&nbsp;&nbsp;&nbsp;&nbsp;factory.setPort(5672);\n\n&nbsp;&nbsp;&nbsp;&nbsp;// 创建连接和通道\n&nbsp;&nbsp;&nbsp;&nbsp;Connection connection = factory.newConnection();\n&nbsp;&nbsp;&nbsp;&nbsp;Channel channel = connection.createChannel();\n&nbsp;&nbsp;&nbsp;&nbsp;//绑定交换机\n&nbsp;&nbsp;&nbsp;&nbsp;channel.exchangeDeclare(\"logs\", \"fanout\");\n&nbsp;&nbsp;&nbsp;&nbsp;//创建临时队列\n&nbsp;&nbsp;&nbsp;&nbsp;String queue = channel.queueDeclare().getQueue();\n&nbsp;&nbsp;&nbsp;&nbsp;//将临时队列绑定exchange\n&nbsp;&nbsp;&nbsp;&nbsp;channel.queueBind(queue, \"logs\", \"\");\n&nbsp;&nbsp;&nbsp;&nbsp;//处理消息\n&nbsp;&nbsp;&nbsp;&nbsp;channel.basicConsume(queue, true, new DefaultConsumer(channel) {\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;@Override\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;public void handleDelivery(String consumerTag, Envelope envelope, AMQP.BasicProperties properties, byte[] body) throws IOException {\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(\"消费者2: \" + new String(body));\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;});\n&nbsp;&nbsp;}\n}\n</pre><p><br></p>', 0, '', '白易', '2023-11-30 11:27:12', '超级管理员', 'RabbitMq教程（消息的发布与订阅）', 'c864ceb6e258c75d183ba54a2033bd77');
