INSERT INTO `ape_operate_log` VALUES ('1733761708149538818', '分页获取笔记', 7, 'com.ape.apeadmin.controller.article.ApeArticleController.getApeArticlePage()', 'POST', 1, '769dc998d617bff4e30c4bde94f37e90', '文西', 'wenxi', '2023-12-10 16:12:58', '/article/getApeArticlePage', '127.0.0.1', NULL, '{}', '{\"code\":1000,\"data\":{\"current\":1,\"hitCount\":false,\"optimizeCountSql\":true,\"orders\":[],\"pages\":1,\"records\":[{\"articleDesc\":\"实现将指定的多个文件打包成一个压缩文件下载。\",\"avatar\":\"/img/1697436716646531073.jpg\",\"content\":\"<h3>1. 引入pom依赖</h3><pre class=\\\"ql-syntax\\\" spellcheck=\\\"false\\\">&lt;dependencies&gt;\\n&nbsp;&nbsp;&lt;!-- Spring Web --&gt;\\n&nbsp;&nbsp;&lt;dependency&gt;\\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;org.springframework.boot&lt;/groupId&gt;\\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;spring-boot-starter-web&lt;/artifactId&gt;\\n&nbsp;&nbsp;&lt;/dependency&gt;\\n&nbsp;&nbsp;&lt;!-- Apache Commons IO --&gt;\\n&nbsp;&nbsp;&lt;dependency&gt;\\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;commons-io&lt;/groupId&gt;\\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;commons-io&lt;/artifactId&gt;\\n&nbsp;&nbsp;&lt;/dependency&gt;\\n&nbsp;&nbsp;&lt;!-- Apache Commons Compress --&gt;\\n&nbsp;&nbsp;&lt;dependency&gt;\\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;org.apache.commons&lt;/groupId&gt;\\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;commons-compress&lt;/artifactId&gt;\\n&nbsp;&nbsp;&lt;/dependency&gt;\\n&lt;/dependencies&gt;\\n</pre><h3>2. 编写控制器</h3><pre class=\\\"ql-syntax\\\" spellcheck=\\\"false\\\">import org.apache.commons.compress.archivers.ArchiveEntry;\\nimport org.apache.commons.compress.archivers.zip.ZipArchiveEntry;\\nimport org.apache.commons.compress.archivers.zip.ZipArchiveOutputStream;\\nimport org.springframework.http.HttpHeaders;\\nimport org.springframework.http.HttpStatus;\\nimport org.springframework.http.ResponseEntity;\\nimport org.springframework.web.bind.annotation.GetMapping;\\nimport org.springframework.web.bind.annotation.RequestParam;\\nimport org.springframework.web.bind.annotation.RestController;\\n&nbsp;\\nimport java.io.File;\\nimport java.io.FileInputStream;\\nimport java.io.FileOutputStream;\\nimport java.io.IOException;\\nimport java.util.List;\\n&nbsp;\\n@RestController\\npublic class FileDownloadController {\\n&nbsp;\\n&nbsp;&nbsp;@GetMapping(\\\"/download\\\")\\n&nbsp;&nbsp;public ResponseEntity&lt;byte[]&gt; downloadFiles(@RequestParam List&lt;String&gt; fileNames) throws IOException {\\n&nbsp;&nbsp;&nbsp;&nbsp;// 创建临时压缩文件\\n&nbsp;&nbsp;&nbsp;&nbsp;File zipFile = File.createTempFile(\\\"download\\\", \\\".zip\\\");\\n&nbsp;&nbsp;&nbsp;&nbsp;try (FileOutputStream fos = new FileOutputStream(zipFile);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ZipArchiveOutputStream zos = new ZipArchiveOutputStream(fos)) {\\n&nbsp;\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for (String fileName : fileNames) {\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;File fileToZip = new File(fileName);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;FileInputStream fis = new FileInputStream(fileToZip);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ArchiveEntry entry = new ZipArchiveEntry(fileToZip.getName());\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;zos.putArchiveEntry(entry);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;byte[] buffer = new byte[1024];\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int len;\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;while ((len = fis.read(buffer)) &gt; 0) {\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;zos.write(buffer, 0, len);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fis.close();\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;zos.closeArchiveEntry();\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\\n&nbsp;\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;zos.finish();\\n&nbsp;&nbsp;&nbsp;&nbsp;}\\n&nbsp;\\n&nbsp;&nbsp;&nbsp;&nbsp;// 设置HTTP响应头\\n&nbsp;&nbsp;&nbsp;&nbsp;HttpHeaders headers = new HttpHeaders();\\n&nbsp;&nbsp;&nbsp;&nbsp;headers.add(\\\"Content-Disposition\\\", \\\"attachment; filename=download.zip\\\");\\n&nbsp;\\n&nbsp;&nbsp;&nbsp;&nbsp;// 读取压缩文件内容并返回给客户端\\n&nbsp;&nbsp;&nbsp;&nbsp;byte[] fileData = org.apache.commons.io.FileUtils.readFileToByteArray(zipFile);\\n&nbsp;\\n&nbsp;&nbsp;&nbsp;&nbsp;return ResponseEntity\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;.status(HttpStatus.OK)\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;.headers(headers)\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;.body(fileData);\\n&nbsp;&nbsp;}\\n}\\n</pre><h3>3. 发送GET请求</h3>\",\"createBy\":\"白易\",\"createTime\":\"2023-11-30 10:15:52\",\"id\":\"1730047963766239234\",\"remark\":\"\",\"state\":0,\"taskId\":\"1729771050996895745\",\"taskName\":\"SpringBoot深入浅出大师课\",\"title\":\"SpringBoot实现文件批量打包下载\",\"updateBy\":\"超级管理员\",\"updateTime\":\"2023-12-03 23:10:45\",\"userId\":\"c864ceb6e258c75d183ba54a2033bd77\"},{\"articleDesc\":\"uniapp：九宫格，简单易上手，拿来即用\",\"avatar\":\"/img/1697436716646531073.jpg\",\"content\":\"<pre class=\\\"ql-syntax\\\" spellcheck=\\\"false\\\">&lt;template&gt;\\n\\t&lt;view class=\\\"index\\\"&gt;\\n\\t\\t&lt;u-navbar title=\\\"\\\" :is-back=\\\"false\\\" :border-bottom=\\\"false\\\" title-color=\\\"#333\\\" :background=\\\"{background:\'\'}\\\"&gt;\\n\\t\\t\\t&lt;view class=\\\"page_navbar_warp\\\"&gt;\\n\\t\\t\\t\\t&lt;image src=\\\"../../static/icon/0.png\\\" mode=\\\"\\\" class=\\\"page_navbar_commonImg\\\" @click=\\\"$go(1,1)\\\"&gt;&lt;/image&gt;\\n\\t\\t\\t&lt;/view&gt;\\n\\t\\t&lt;/u-navbar&gt;\\n \\n\\t\\t&lt;div class=\\\"gameBox\\\"&gt;\\n\\t\\t\\t&lt;view class=\\\"top\\\"&gt;\\n\\t\\t\\t\\t&lt;view class=\\\"user\\\"&gt;\\n\\t\\t\\t\\t\\t&lt;view class=\\\"left\\\"&gt;\\n\\t\\t\\t\\t\\t\\t&lt;image src=\\\"../../static/logo.png\\\" mode=\\\"\\\"&gt;&lt;/image&gt;\\n\\t\\t\\t\\t\\t\\t&lt;view class=\\\"text\\\"&gt;某某某店铺&lt;/view&gt;\\n\\t\\t\\t\\t\\t&lt;/view&gt;\\n\\t\\t\\t\\t\\t&lt;view class=\\\"right\\\"&gt;进入店铺&lt;/view&gt;\\n\\t\\t\\t\\t&lt;/view&gt;\\n\\t\\t\\t\\t&lt;view class=\\\"num\\\"&gt;剩余抽奖次数：1&lt;/view&gt;\\n\\t\\t\\t&lt;/view&gt;\\n\\t\\t\\t&lt;view class=\\\"content\\\"&gt;\\n\\t\\t\\t\\t&lt;image src=\\\"../../static/img/196.png\\\" mode=\\\"\\\" class=\\\"start\\\" @click=\\\"move\\\"&gt;&lt;/image&gt;\\n\\t\\t\\t\\t&lt;view class=\\\"ul\\\"&gt;\\n\\t\\t\\t\\t&nbsp;&nbsp;&lt;view v-for=\\\"(item,i) in list\\\" :key=\\\"i\\\" class=\\\"li\\\" :class=\\\"[\'item\' + (i+1), {\'active\': index == i}]\\\"&gt;\\n\\t\\t\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&lt;image src=\\\"../../static/img/199.png\\\" mode=\\\"\\\"&gt;&lt;/image&gt;\\n\\t\\t\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&lt;view class=\\\"text1\\\"&gt;+{{item.number}}{{item.prize_name}}&lt;/view&gt;\\n\\t\\t\\t\\t&nbsp;&nbsp;&lt;/view&gt;\\n\\t\\t\\t\\t&lt;/view&gt;\\n\\t\\t\\t&lt;/view&gt;\\n\\t\\t\\t&lt;view class=\\\"bom\\\"&gt;\\n\\t\\t\\t\\t&lt;image src=\\\"../../static/img/193.png\\\" mode=\\\"\\\"&gt;&lt;/image&gt;\\n\\t\\t\\t\\t&lt;image src=\\\"../../static/img/194.png\\\" mode=\\\"\\\"&gt;&lt;/image&gt;\\n\\t\\t\\t&lt;/view&gt;\\n\\t\\t&lt;/div&gt;\\n\\t\\t&lt;view class=\\\"list\\\"&gt;\\n\\t\\t\\t&lt;view class=\\\"box\\\"&gt;\\n\\t\\t\\t\\t&lt;view class=\\\"title\\\"&gt;中奖名单&lt;/view&gt;\\n\\t\\t\\t\\t&lt;view class=\\\"item\\\" v-for=\\\"(item,index) in 13\\\" :key=\\\"index\\\"&gt;\\n\\t\\t\\t\\t\\t&lt;view class=\\\"text\\\"&gt;4-13 13:21&lt;/view&gt;\\n\\t\\t\\t\\t\\t&lt;view class=\\\"text\\\"&gt;用户昵称&lt;/view&gt;\\n\\t\\t\\t\\t\\t&lt;view class=\\\"text\\\"&gt;华为手机*1&lt;/view&gt;\\n\\t\\t\\t\\t&lt;/view&gt;\\n\\t\\t\\t&lt;/view&gt;\\n\\t\\t&lt;/view&gt;\\n\\n\\t&lt;/view&gt;\\n&lt;/template&gt;\\n\\n&lt;script&gt;\\n\\texport default {\\n\\t\\tdata() {\\n\\t\\t&nbsp;&nbsp;return {\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;list: [],//奖品列表\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;index: 0, // 当前转动到哪个位置，第一次起点位置0,对应页面item1位置\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;count: 8, // 总共有多少个位置\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;times: 0, // 转动跑格子次数,\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;cycle: 100, // 转动基本次数：即至少需要转动多少次再进入抽奖环节\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;speed: 40, // 初始转动速度\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;timer: 0, // 转动定时器\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;prize: 0, // 中奖位置，接口返回\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;number_of_draws:0,//限制每天抽奖次数，接口返回\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;prize_data: {//中奖信息\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;id:Number,//奖品ID\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;name:\'\',//奖品名称\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;number:Number,//奖品数量\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;image:\'\',//奖品图片\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;type:Number,// 奖品类型\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;},\\n\\t\\t&nbsp;&nbsp;}\\n\\t\\t},\\n\\t\\tmounted() {\\n\\t\\t&nbsp;&nbsp;//获取奖品列表\\n\\t\\t\\tthis.list = [\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:1,\\n\\t\\t\\t\\t\\tnumber:1,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t},\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:2,\\n\\t\\t\\t\\t\\tnumber:2,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t},\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:3,\\n\\t\\t\\t\\t\\tnumber:3,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t},\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:4,\\n\\t\\t\\t\\t\\tnumber:4,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t},\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:5,\\n\\t\\t\\t\\t\\tnumber:5,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t},\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:6,\\n\\t\\t\\t\\t\\tnumber:6,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t},\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:7,\\n\\t\\t\\t\\t\\tnumber:7,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t},\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:8,\\n\\t\\t\\t\\t\\tnumber:8,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t}\\n\\t\\t\\t]; // 奖品列表数据\\n\\t\\t\\tthis.number_of_draws = 2; // 该用户剩余抽奖次数\\n\\t\\t},\\n\\t\\tmethods: {\\n\\t\\t&nbsp;&nbsp;//点击开始抽奖\\n\\t\\t&nbsp;&nbsp;move() {\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;if( this.number_of_draws == 0){\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.$toast(\'今日抽奖次数已用完,明天再来吧\');\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;}else if(this.times != 0){\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.$toast(\'正在抽奖中，请勿重复点击\')\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;} else{\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.number_of_draws--;//抽奖开始，次数-1\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.speed = 40;//每次抽奖速度初始化为200\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.prize_data = 1;//已经拿到中奖信息，页面展示，等抽奖结束后，将弹窗弹出\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.prize = 1;//中奖位置赋值，跑马灯最终停留位置，这里实际位置需要-1\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.startRoll();//执行抽奖\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;}\\n\\t\\t&nbsp;&nbsp;},\\n\\t\\t&nbsp;&nbsp;// 开始转动\\n\\t\\t&nbsp;&nbsp;startRoll() {\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;this.times += 1; // 转动次数\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;this.oneRoll(); // 转动过程调用的每一次转动方法，这里是第一次调用初始化\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;this.usePrize();\\n\\t\\t&nbsp;&nbsp;},\\n\\t\\t&nbsp;&nbsp;&nbsp;\\n\\t\\t&nbsp;&nbsp;// 每一次转动\\n\\t\\t&nbsp;&nbsp;oneRoll() {\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;let index = this.index; // 当前转动到哪个位置\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;const count = 8; // 总共有多少个位置\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;index += 1;\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;if (index &gt; count - 1) {\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;index = 0;\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;}\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;this.index = index;\\n\\t\\t&nbsp;&nbsp;},\\n \\n\\t\\t&nbsp;&nbsp;usePrize() {\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;// 如果当前转动次数达到要求 &amp;&amp; 目前转到的位置是中奖位置\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;if (this.times &gt; this.cycle-20 &amp;&amp; this.prize === this.index) {\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;clearTimeout(this.timer); // 清除转动定时器\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.times = 0; // 转动跑格子次数初始化为0，可以开始下次抽奖\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(this.prize_data.type == 0){\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;c