\\t&nbsp;&nbsp;&nbsp;&nbsp;cycle: 100, // 转动基本次数：即至少需要转动多少次再进入抽奖环节\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;speed: 40, // 初始转动速度\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;timer: 0, // 转动定时器\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;prize: 0, // 中奖位置，接口返回\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;number_of_draws:0,//限制每天抽奖次数，接口返回\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;prize_data: {//中奖信息\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;id:Number,//奖品ID\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;name:\'\',//奖品名称\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;number:Number,//奖品数量\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;image:\'\',//奖品图片\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;type:Number,// 奖品类型\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;},\\n\\t\\t&nbsp;&nbsp;}\\n\\t\\t},\\n\\t\\tmounted() {\\n\\t\\t&nbsp;&nbsp;//获取奖品列表\\n\\t\\t\\tthis.list = [\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:1,\\n\\t\\t\\t\\t\\tnumber:1,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t},\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:2,\\n\\t\\t\\t\\t\\tnumber:2,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t},\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:3,\\n\\t\\t\\t\\t\\tnumber:3,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t},\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:4,\\n\\t\\t\\t\\t\\tnumber:4,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t},\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:5,\\n\\t\\t\\t\\t\\tnumber:5,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t},\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:6,\\n\\t\\t\\t\\t\\tnumber:6,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t},\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:7,\\n\\t\\t\\t\\t\\tnumber:7,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t},\\n\\t\\t\\t\\t{\\n\\t\\t\\t\\t\\tid:8,\\n\\t\\t\\t\\t\\tnumber:8,\\n\\t\\t\\t\\t\\tprize_name:\'乐豆\'\\n\\t\\t\\t\\t}\\n\\t\\t\\t]; // 奖品列表数据\\n\\t\\t\\tthis.number_of_draws = 2; // 该用户剩余抽奖次数\\n\\t\\t},\\n\\t\\tmethods: {\\n\\t\\t&nbsp;&nbsp;//点击开始抽奖\\n\\t\\t&nbsp;&nbsp;move() {\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;if( this.number_of_draws == 0){\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.$toast(\'今日抽奖次数已用完,明天再来吧\');\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;}else if(this.times != 0){\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.$toast(\'正在抽奖中，请勿重复点击\')\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;} else{\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.number_of_draws--;//抽奖开始，次数-1\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.speed = 40;//每次抽奖速度初始化为200\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.prize_data = 1;//已经拿到中奖信息，页面展示，等抽奖结束后，将弹窗弹出\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.prize = 1;//中奖位置赋值，跑马灯最终停留位置，这里实际位置需要-1\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.startRoll();//执行抽奖\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;}\\n\\t\\t&nbsp;&nbsp;},\\n\\t\\t&nbsp;&nbsp;// 开始转动\\n\\t\\t&nbsp;&nbsp;startRoll() {\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;this.times += 1; // 转动次数\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;this.oneRoll(); // 转动过程调用的每一次转动方法，这里是第一次调用初始化\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;this.usePrize();\\n\\t\\t&nbsp;&nbsp;},\\n\\t\\t&nbsp;&nbsp;&nbsp;\\n\\t\\t&nbsp;&nbsp;// 每一次转动\\n\\t\\t&nbsp;&nbsp;oneRoll() {\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;let index = this.index; // 当前转动到哪个位置\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;const count = 8; // 总共有多少个位置\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;index += 1;\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;if (index &gt; count - 1) {\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;index = 0;\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;}\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;this.index = index;\\n\\t\\t&nbsp;&nbsp;},\\n \\n\\t\\t&nbsp;&nbsp;usePrize() {\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;// 如果当前转动次数达到要求 &amp;&amp; 目前转到的位置是中奖位置\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;if (this.times &gt; this.cycle-20 &amp;&amp; this.prize === this.index) {\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;clearTimeout(this.timer); // 清除转动定时器\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.times = 0; // 转动跑格子次数初始化为0，可以开始下次抽奖\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(this.prize_data.type == 0){\\n\\t\\t&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;c