INSERT INTO `ape_operate_log` VALUES ('1744668246707814403', '分页获取课程', 7, 'com.ape.apeadmin.controller.task.ApeTaskController.getApeTaskPage()', 'POST', 1, '769dc998d617bff4e30c4bde94f37e90', '文西', 'wenxi', '2024-01-09 18:31:39', '/task/getApeTaskPage', '127.0.0.1', NULL, '{}', '{\"code\":1000,\"data\":{\"current\":1,\"hitCount\":false,\"optimizeCountSql\":true,\"orders\":[],\"pages\":2,\"records\":[{\"classification\":\"Spring\",\"createBy\":\"白易\",\"createTime\":\"2023-11-29 15:36:09\",\"id\":\"1729766176024518658\",\"image\":\"/img/1701243304666.png\",\"major\":\"计算机网络\",\"name\":\"Spring框架全套教程\",\"num\":1,\"proportion\":70,\"remark\":\"\",\"state\":0,\"taskDescribe\":\"spring框架是于2003年兴起的一个轻量级的java开发框架,有Rod Johnson创建,是针对bean的生命周期进行管理的轻量级容器,使现有的技术更加容易使用,简单来说Spring就是一个轻量级的控制反转(IOC)和面向切面编程(AOP)的框架 !\",\"teacherId\":\"c864ceb6e258c75d183ba54a2033bd77\",\"teacherName\":\"白易\",\"updateBy\":\"超级管理员\",\"updateTime\":\"2023-11-30 19:12:18\"},{\"classification\":\"Mybatis\",\"createBy\":\"超级管理员\",\"createTime\":\"2023-11-29 15:40:36\",\"id\":\"1729767297673674754\",\"image\":\"/img/1701243625276.jpg\",\"major\":\"计算机科学与技术\",\"name\":\"Mybatis详细教程入门\",\"num\":1,\"proportion\":70,\"remark\":\"\",\"state\":0,\"taskDescribe\":\"Mybatis是一款优秀的持久层框架,支持定制化 SQL、存储过程以及高级映射的优秀的持久层框架。MyBatis 避免了几乎所有的 JDBC 代码和手动设置参数以及获取结果集。MyBatis 可以对配置和原生Map使用简单的 XML 或注解,将接口和 Java 的 POJOs(Plain Old Java Objects,普通的 Java对象)映射成数据库中的记录。\",\"teacherId\":\"c864ceb6e258c75d183ba54a2033bd77\",\"teacherName\":\"白易\",\"updateBy\":\"超级管理员\",\"updateTime\":\"2023-11-30 19:12:21\"},{\"classification\":\"Vue\",\"createBy\":\"超级管理员\",\"createTime\":\"2023-11-29 15:41:40\",\"id\":\"1729767565484179458\",\"image\":\"/img/1701243695300.png\",\"major\":\"信息技术管理\",\"name\":\"Vue从零开始全套教程\",\"num\":2,\"proportion\":70,\"remark\":\"\",\"state\":0,\"taskDescribe\":\"Vue是于2013年(与React框架同年发布)推出的一个渐进式、自底向上的前端框架,它的作者叫尤雨溪。那么什么叫做渐进式框架呢?比较官方的说法就是:以Vue内核作为核心,随着业务的深入、需求的递增,可以使用其周边生态(vue-router、vuex、ssr等)深度应用到项目中。那么通俗上来讲:就是我们可以使用vue的