INSERT INTO `ape_operate_log` VALUES ('1747797771679514625', '分页获取笔记', 7, 'com.ape.apeadmin.controller.article.ApeArticleController.getApeArticlePage()', 'POST', 1, '769dc998d617bff4e30c4bde94f37e90', '文西', 'wenxi', '2024-01-18 09:47:16', '/article/getApeArticlePage', '127.0.0.1', NULL, '{}', '{\"code\":1000,\"data\":{\"current\":1,\"hitCount\":false,\"optimizeCountSql\":true,\"orders\":[],\"pages\":1,\"records\":[{\"articleDesc\":\"实现将指定的多个文件打包成一个压缩文件下载。\",\"avatar\":\"/img/1697436716646531073.jpg\",\"content\":\"<h3>1. 引入pom依赖</h3><pre class=\\\"ql-syntax\\\" spellcheck=\\\"false\\\">&lt;dependencies&gt;\\n&nbsp;&nbsp;&lt;!-- Spring Web --&gt;\\n&nbsp;&nbsp;&lt;dependency&gt;\\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;org.springframework.boot&lt;/groupId&gt;\\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;spring-boot-starter-web&lt;/artifactId&gt;\\n&nbsp;&nbsp;&lt;/dependency&gt;\\n&nbsp;&nbsp;&lt;!-- Apache Commons IO --&gt;\\n&nbsp;&nbsp;&lt;dependency&gt;\\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;commons-io&lt;/groupId&gt;\\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;commons-io&lt;/artifactId&gt;\\n&nbsp;&nbsp;&lt;/dependency&gt;\\n&nbsp;&nbsp;&lt;!-- Apache Commons Compress --&gt;\\n&nbsp;&nbsp;&lt;dependency&gt;\\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;groupId&gt;org.apache.commons&lt;/groupId&gt;\\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;artifactId&gt;commons-compress&lt;/artifactId&gt;\\n&nbsp;&nbsp;&lt;/dependency&gt;\\n&lt;/dependencies&gt;\\n</pre><h3>2. 编写控制器</h3><pre class=\\\"ql-syntax\\\" spellcheck=\\\"false\\\">import org.apache.commons.compress.archivers.ArchiveEntry;\\nimport org.apache.commons.compress.archivers.zip.ZipArchiveEntry;\\nimport org.apache.commons.compress.archivers.zip.ZipArchiveOutputStream;\\nimport org.springframework.http.HttpHeaders;\\nimport org.springframework.http.HttpStatus;\\nimport org.springframework.http.ResponseEntity;\\nimport org.springframework.web.bind.annotation.GetMapping;\\nimport org.springframework.web.bind.annotation.RequestParam;\\nimport org.springframework.web.bind.annotation.RestController;\\n&nbsp;\\nimport java.io.File;\\nimport java.io.FileInputStream;\\nimport java.io.FileOutputStream;\\nimport java.io.IOException;\\nimport java.util.List;\\n&nbsp;\\n@RestController\\npublic class FileDownloadController {\\n&nbsp;\\n&nbsp;&nbsp;@GetMapping(\\\"/download\\\")\\n&nbsp;&nbsp;public ResponseEntity&lt;byte[]&gt; downloadFiles(@RequestParam List&lt;String&gt; fileNames) throws IOException {\\n&nbsp;&nbsp;&nbsp;&nbsp;// 创建临时压缩文件\\n&nbsp;&nbsp;&nbsp;&nbsp;File zipFile = File.createTempFile(\\\"download\\\", \\\".zip\\\");\\n&nbsp;&nbsp;&nbsp;&nbsp;try (FileOutputStream fos = new FileOutputStream(zipFile);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ZipArchiveOutputStream zos = new ZipArchiveOutputStream(fos)) {\\n&nbsp;\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for (String fileName : fileNames) {\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;File fileToZip = new File(fileName);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;FileInputStream fis = new FileInputStream(fileToZip);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ArchiveEntry entry = new ZipArchiveEntry(fileToZip.getName());\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;zos.putArchiveEntry(entry);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;byte[] buffer = new byte[1024];\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int len;\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;while ((len = fis.read(buffer)) &gt; 0) {\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;zos.write(buffer, 0, len);\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fis.close();\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;zos.closeArchiveEntry();\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\\n&nbsp;\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;zos.finish();\\n&nbsp;&nbsp;&nbsp;&nbsp;}\\n&nbsp;\\n&nbsp;&nbsp;&nbsp;&nbsp;// 设置HTTP响应头\\n&nbsp;&nbsp;&nbsp;&nbsp;HttpHeaders headers = new HttpHeaders();\\n&nbsp;&nbsp;&nbsp;&nbsp;headers.add(\\\"Content-Disposition\\\", \\\"attachment; filename=download.zip\\\");\\n&nbsp;\\n&nbsp;&nbsp;&nbsp;&nbsp;// 读取压缩文件内容并返回给客户端\\n&nbsp;&nbsp;&nbsp;&nbsp;byte[] fileData = org.apache.commons.io.FileUtils.readFileToByteArray(zipFile);\\n&nbsp;\\n&nbsp;&nbsp;&nbsp;&nbsp;return ResponseEntity\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;.status(HttpStatus.OK)\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;.headers(headers)\\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;.body(fileData);\\n&nbsp;&nbsp;}\\n}\\n</pre><h3>3. 发送GET请求</h3>\",\"createBy\":\"白易\",\"createTime\":\"2023-11-30 10:15:52\",\"id\":\"1730047963766239234\",\"remark\":\"\",\"state\":0,\"taskId\":\"1729771050996895745\",\"taskName\":\"SpringBoot深入浅出大师课\",\"title\":\"SpringBoot实现文件批量打包下载\",\"updateBy\":\"超级管理员\",\"updateTime\":\"2023-12-03 23:10:45\",\"userId\":\"c864ceb6e258c75d183ba54a2033bd77\"},{\"articleDesc\":\"uniapp：九宫格，简单易上手，拿来即用\",\"avatar\":\"/img/1697436716646531073.jpg\",\"content\":\"<pre class=\\\"ql-syntax\\\" spellcheck=\\\"false\\\">&lt;template&gt;\\n\\t&lt;view class=\\\"index\\\"&gt;\\n\\t\\t&lt;u-navbar title=\\\"\\\" :is-back=\\\"false\\\" :border-bottom=\\\"false\\\" title-color=\\\"#333\\\" :background=\\\"{background:\'\'}\\\"&gt;\\n\\t\\t\\t&lt;view class=\\\"page_navbar_warp\\\"&gt;\\n\\t\\t\\t\\t&lt;image src=\\\"../../static/icon/0.png\\\" mode=\\\"\\\" class=\\\"page_navbar_commonImg\\\" @click=\\\"$go(1,1)\\\"&gt;&lt;/image&gt;\\n\\t\\t\\t&lt;/view&gt;\\n\\t\\t&lt;/u-navbar&gt;\\n \\n\\t\\t&lt;div class=\\\"gameBox\\\"&gt;\\n\\t\\t\\t&lt;view class=\\\"top\\\"&gt;\\n\\t\\t\\t\\t&lt;view class=\\\"user\\\"&gt;\\n\\t\\t\\t\\t\\t&lt;view class=\\\"left\\\"&gt;\\n\\t\\t\\t\\t\\t\\t&lt;image src=\\\"../../static/logo.png\\\" mode=\\\"\\\"&gt;&lt;/image&gt;\\n\\t\\t\\t\\t\\t\\t&lt;view class=\\\"text\\\"&gt;某某某店铺&lt;/view&gt;\\n\\t\\t\\t\\t\\t&lt;/view&gt;\\n\\t\\t\\t\\t\\t&lt;view class=\\\"right\\\"&gt;进入店铺&lt;/view&gt;