INSERT INTO `ape_operate_log` VALUES ('1747821681758285827', '预览', 7, 'com.ape.apeadmin.controller.gen.ApeGenTableController.preview()', 'GET', 1, '1697436716646531073', '超级管理员', 'admin', '2024-01-18 11:22:17', '/genTable/preview', '127.0.0.1', NULL, '{\"random\":[\"0.8930224589659874\"],\"id\":[\"1747819236541968385\"]}', '{\"code\":1000,\"data\":{\"domain\":\"package com.ape.apesystem.domain;\\n\\nimport com.ape.apecommon.domain.BaseEntity;\\nimport com.baomidou.mybatisplus.annotation.*;\\nimport lombok.AllArgsConstructor;\\nimport lombok.Data;\\nimport java.util.Date;\\nimport java.io.Serializable;\\nimport lombok.EqualsAndHashCode;\\nimport lombok.NoArgsConstructor;\\nimport lombok.experimental.Accessors;\\n\\n/**\\n * @author 超级管理员\\n * @version 1.0\\n * @description: 答疑\\n * @date 2024/01/18 11:22\\n */\\n@Data\\n@AllArgsConstructor\\n@NoArgsConstructor\\n@Accessors(chain = true)\\n@TableName(\\\"ape_question\\\")\\npublic class ApeQuestion implements Serializable {\\n\\n     private static final long serialVersionUID = 1L;\\n\\n     @TableId(value = \\\"id\\\", type = IdType.ASSIGN_ID)\\n     private String id;\\n\\n     /**\\n     * 用户id\\n     */\\n     private String userId;\\n\\n     /**\\n     * 用户昵称\\n     */\\n     private String userName;\\n\\n     /**\\n     * 提问内容\\n     */\\n     private String content;\\n\\n     /**\\n     * 回复\\n     */\\n     private String answer;\\n\\n     /**\\n     * 教师id\\n     */\\n     private String teacherId;\\n\\n     /**\\n     * 课程id\\n     */\\n     private String taskId;\\n\\n     /**\\n     * 课程名称\\n     */\\n     private String taskName;\\n\\n     @TableField(exist = false)\\n     private Integer pageNumber;\\n\\n     @TableField(exist = false)\\n     private Integer pageSize;\\n}\",\"domainName\":\"ApeQuestion\",\"mapper\":\"package com.ape.apesystem.mapper;\\n\\nimport com.ape.apesystem.domain.ApeQuestion;\\nimport com.baomidou.mybatisplus.core.mapper.BaseMapper;\\n\\n/**\\n * @author 超级管理员\\n * @version 1.0\\n * @description: 答疑mapper\\n * @date 2024/01/18 11:22\\n */\\npublic interface ApeQuestionMapper extends BaseMapper<ApeQuestion> {\\n}\",\"mapperName\":\"ApeQuestionMapper\",\"service\":\"package com.ape.apesystem.service;\\n\\nimport com.ape.apesystem.domain.ApeQuestion;\\nimport com.baomidou.mybatisplus.extension.service.IService;\\n\\n/**\\n * @author 超级管理员\\n * @version 1.0\\n * @description: 答疑service\\n * @date 2024/01/18 11:22\\n */\\npublic interface ApeQuestionService extends IService<ApeQuestion> {\\n}\",\"serviceName\":\"ApeQuestionService\",\"serviceImpl\":\"package com.ape.apesystem.service.impl;\\n\\nimport com.ape.apesystem.domain.ApeQuestion;\\nimport com.ape.apesystem.mapper.ApeQuestionMapper;\\nimport com.ape.apesystem.service.ApeQuestionService;\\nimport com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;\\nimport org.springframework.stereotype.Service;\\n\\n/**\\n * @author 超级管理员\\n * @version 1.0\\n * @description: 答疑service实现类\\n * @date 2024/01/18 11:22\\n */\\n@Service\\npublic class ApeQuestionServiceImpl extends ServiceImpl<ApeQuestionMapper, ApeQuestion> implements ApeQuestionService {\\n}\",\"serviceImplName\":\"ApeQuestionServiceImpl\",\"controller\":\"package com.ape.apeadmin.controller.question;\\n\\nimport com.ape.apecommon.annotation.Log;\\nimport com.ape.apecommon.domain.Result;\\nimport com.ape.apecommon.enums.BusinessType;\\nimport com.ape.apecommon.enums.ResultCode;\\nimport com.ape.apesystem.domain.ApeQuestion;\\nimport com.ape.apesystem.service.ApeQuestionService;\\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\\nimport org.apache.commons.lang3.StringUtils;\\nimport org.springframework.beans.factory.annotation.Autowired;\\nimport org.springframework.stereotype.Controller;\\nimport org.springframework.transaction.annotation.Transactional;\\nimport org.springframework.web.bind.annotation.*;\\n\\n/**\\n * @author 超级管理员\\n * @version 1.0\\n * @description: 答疑controller\\n * @date 2024/01/18 11:22\\n */\\n@Controller\\n@ResponseBody\\n@RequestMapping(\\\"question\\\")\\npublic class ApeQuestionController {\\n\\n     @Autowired\\n     private ApeQuestionService apeQuestionService;\\n\\n     /** 分页获取答疑 */\\n     @Log(name = \\\"分页获取答疑\\\", type = BusinessType.OTHER)\\n     @PostMapping(\\\"getApeQuestionPage\\\")\\n     public Result getApeQuestionPage(@RequestBody ApeQuestion apeQuestion) {\\n         Page<ApeQuestion> page = new Page<>(apeQuestion.getPageNumber(),apeQuestion.getPageSize());\\n         QueryWrapper<ApeQuestion> queryWrapper = new QueryWrapper<>();\\n         queryWrapper.lambda()\\n                .like(StringUtils.isNotBlank(apeQuestion.getUserName()),ApeQuestion::getUserName,apeQuestion.getUserName())\\n                .like(StringUtils.isNotBlank(apeQuestion.getContent()),ApeQuestion::getContent,apeQuestion.getContent())\\n                .like(StringUtils.isNotBlank(apeQuestion.getAnswer()),ApeQuestion::getAnswer,apeQuestion.getAnswer())\\n                .eq(StringUtils.isNotBlank(apeQuestion.getTeacherId()),ApeQuestion::getTeacherId,apeQuestion.getTeacherId())\\n                .like(StringUtils.isNotBlank(apeQuestion.getTaskName()),ApeQuestion::getTaskName,apeQuestion.getTaskName());\\n         Page<ApeQuestion> apeQuestionPage = apeQuestionService.page(page, queryWrapper);\\n         return Result.success(apeQuestionPage);\\n    }\\n\\n     /** 根据id获取答疑 */\\n     @Log(name = \\\"根据id获取答疑\\\", type = BusinessType.OTHER)\\n     @GetMapping(\\\"getApeQuestionById\\\")\\n     public Result getApeQuestionById(@RequestParam(\\\"id\\\")String id) {\\n         ApeQuestion apeQuestion = apeQuestionService.getById(id);\\n         return Result.success(apeQuestion);\\n     }\\n\\n     /** 保存答疑 */\\n     @Log(name = \\\"保存答疑\\\", type = BusinessType.INSERT)\\n     @PostMapping(\\\"saveApeQuestion\\\")\\n     public Result saveApeQuestion(@RequestBody ApeQuestion apeQuestion) {\\n         boolean save = apeQuestionService.save(apeQuestion);\\n         if (save) {\\n             return Result.success();\\n         } else {\\n             return Result.fail(ResultCode.COMMON_DATA_OPTION_ERROR.getMessage());\\n         }\\n     }\\n\\n     /** 编辑答疑 */\\n     @Log(name = \\\"编辑答疑\\\", type = BusinessType.UPDATE)\\n     @PostMapping(\\\"editApeQuestion\\\")\\n     public Result editApeQuestion(@RequestBody ApeQuestion apeQuestion) {\\n         boolean save = apeQuestionService.updateById(apeQuestion);\\n         if (save) {\\n             return Result.success();\\n         } else {\\n             return Result.fail(ResultCode.COMMON_DATA_OPTION_ERROR.getMessage());\\n         }\\n     }\\n\\n     /** 删除答疑 */\\n     @GetMapping(\\\"removeApeQuestion\\\")\\n     @Log(name = \\\"删除答疑\\\", type = BusinessType.DELETE)\\n     public Result removeApeQuestion(@RequestParam(\\\"ids\\\")String ids) {\\n         if (StringUtils.isNotBlank(ids)) {\\n             String[] asList = ids.split(\\\",\\\");\\n             for (String id : asList) {\\n                 apeQuestionService.removeById(id);\\n             }\\n             return Result.success();\\n         } else {\\n             return Result.fail(\\\"答疑id不能为空！\\\");\\n         }\\n     }\\n\\n}\",\"controllerName\":\"ApeQuestionController\",\"mapperXml\":\"<?xml version=\\\"1.0\\\" encoding=\\\"UTF-8\\\" ?>\\n<!DOCTYPE mapper\\n        PUBLIC \\\"-//mybatis.org//DTD Mapper 3.0//EN\\\"\\n        \\\"http://mybatis.org/dtd/mybatis-3-mapper.dtd\\\">\\n<mapper namespace=\\\"com.ape.apesystem.mapper.ApeQuestionMapper\\\">\\n\\n</mapper>\",\"mapperXmlName\":\"ApeQuestionMapper.xml\",\"api\":\"//-------------------------------答疑---------------------------------------\\n//查询答疑\\nexport const getApeQuestionPage = (params) => post(\\\"/question/getApeQuestionPage\\\",params)\\n//根据id查询答疑\\nexport const getApeQuestionById = (params) => get(\\\"/question/getApeQuestionById\\\",params)\\n//保存答疑\\nexport const saveApeQuestion = (params) => post(\\\"/question/saveApeQuestion\\\",params)\\n//更新答疑\\nexport const editApeQuestion = (params) => post(\\\"/question/editApeQuestion\\\",params)\\n//删除ApeQuestion\\nexport const removeApeQuestion = (params) => get(\\\"/question/removeApeQuestion\\\",params)\",\"index\":\"<template>\\n  <div class=\\\"_question\\\">\\n      <div class=\\\"search-table\\\">\\n        <div class=\\\"search\\\">\\n            <el-row :gutter=\\\"10\\\" style=\\\"padding:10px\\\">\\n                <el-col :xs=\\\"24\\\" :sm=\\\"12\\\" :md=\\\"8\\\" :lg=\\\"8\\\" :xl=\\\"8\\\">\\n                    <span class=\\\"search-title\\\">用户昵称:</span>\\n                    <el-input\\n                        style=\\\"margin-top:10px\\\"\\n                        size=\\\"mini\\\"\\n                        placeholder=\\\"请输入用户昵称\\\"\\n                        v-model=\\\"search.userName\\\">\\n                    </el-input>\\n                </el-col>\\n                <el-col :xs=\\\"24\\\" :sm=\\\"12\\\" :md=\\\"8\\\" :lg=\\\"8\\\" :xl=\\\"8\\\">\\n                    <span class=\\\"search-title\\\">提问内容:</span>\\n                    <el-input\\n                        style=\\\"margin-top:10px\\\"\\n                        size=\\\"mini\\\"\\n                        placeholder=\\\"请输入提问内容\\\"\\n                        v-model=\\\"search.content\\\">\\n                    </el-input>\\n                </el-col>\\n                <el-col :xs=\\\"24\\\" :sm=\\\"12\\\" :md=\\\"8\\\" :lg=\\\"8\\\" :xl=\\\"8\\\">\\n                    <span class=\\\"search-title\\\">回复:</span>\\n                    <el-input\\n                        style=\\\"margin-top:10px\\\"\\n                        size=\\\"mini\\\"\\n                        placeholder=\\\"请输入回复\\\"\\n                        v-model=\\\"search.answer\\\">\\n                    </el-input>\\n                </el-col>\\n                <el-col :xs=\\\"24\\\" :sm=\\\"12\\\" :md=\\\"8\\\" :lg=\\\"8\\\" :xl=\\\"8\\\">\\n                    <span class=\\\"search-title\\\">教师id:</span>\\n                    <el-input\\n                        style=\\\"margin-top:10px\\\"\\n                        size=\\\"mini\\\"\\n                        placeholder=\\\"请输入教师id\\\"\\n                        v-model=\\\"search.teacherId\\\">\\n                    </el-input>\\n                </el-col>\\n                <el-col :xs=\\\"24\\\" :sm=\\\"12\\\" :md=\\\"8\\\" :lg=\\\"8\\\" :xl=\\\"8\\\">\\n                    <span class=\\\"search-title\\\">课程名称:</span>\\n                    <el-input\\n                        style=\\\"margin-top:10px\\\"\\n                        size=\\\"mini\\\"\\n                        placeholder=\\\"请输入课程名称\\\"\\n                        v-model=\\\"search.taskName\\\">\\n                    </el-input>\\n                </el-col>\\n                <el-col :xs=\\\"24\\\" :sm=\\\"12\\\" :md=\\\"8\\\" :lg=\\\"8\\\" :xl=\\\"8\\\">\\n                    <el-button style=\\\"margin-top:10px\\\" size=\\\"mini\\\" icon=\\\"el-icon-search\\\" type=\\\"primary\\\" @click=\\\"searchPage\\\">查询</el-button>\\n                    <el-button style=\\\"margin-top:10px\\\" size=\\\"mini\\\" icon=\\\"el-icon-refresh\\\" @click=\\\"refresh\\\">重置</el-button>\\n                </el-col>\\n            </el-row>\\n        </div>\\n        <div class=\\\"table\\\">\\n            <el-row style=\\\"padding-top:10px;margin-left:10px\\\">\\n                <el-button type=\\\"primary\\\" size=\\\"mini\\\" icon=\\\"el-icon-plus\\\" plain @click=\\\"add\\\">新增</el-button>\\n                <el-button type=\\\"success\\\" :disabled=\\\"update.length != 1 ?true:false\\\" size=\\\"mini\\\" icon=\\\"el-icon-edit\\\" plain @click=\\\"updateDataBtn\\\">修改</el-button>\\n                <el-button type=\\\"danger\\\" :disabled=\\\"update.length <= 0 ?true:false\\\" size=\\\"mini\\\" icon=\\\"el-icon-delete\\\" plain @click=\\\"deleteDataBtn\\\">删除</el-button>\\n                <!-- <el-button type=\\\"warning\\\" size=\\\"mini\\\" icon=\\\"el-icon-download\\\" plain>导出</el-button> -->\\n            </el-row>\\n            <el-table\\n            v-loading=\\\"loading\\\"\\n            :data=\\\"tableData\\\"\\n            :header-cell-style=\\\"{\\n              \'color\': \'#4A2B90\',\\n              \'background-color\': \'#ECE9F4\',\\n            }\\\"\\n            :row-style=\\\"{\\n              \'color\': \'#888897\',\\n              \'font-size\': \'15px\',\\n              \'font-family\':\'黑体\',\\n              \'white-space\': \'nowrap\'\\n            }\\\"\\n            @selection-change=\\\"handleSelectionChange\\\"\\n            stripe\\n            style=\\\"width: 100%\\\">\\n            <el-table-column\\n              type=\\\"selection\\\"\\n              width=\\\"55\\\">\\n            </el-table-column>\\n            <el-table-column\\n              prop=\\\"userName\\\"\\n              label=\\\"用户昵称\\\"\\n              width=\\\"180\\\"\\n              >\\n            </el-table-column>\\n            <el-table-column\\n              prop=\\\"content\\\"\\n              label=\\\"提问内容\\\"\\n              width=\\\"180\\\"\\n              >\\n            </el-table-column>\\n            <el-table-column\\n              prop=\\\"answer\\\"\\n              label=\\\"回复\\\"\\n              width=\\\"180\\\"\\n              >\\n            </el-table-column>\\n            <el-table-column\\n              prop=\\\"taskName\\\"\\n              label=\\\"课程名称\\\"\\n              width=\\\"180\\\"\\n              >\\n            </el-table-column>\\n            <el-table-column\\n              label=\\\"操作\\\"\\n              width=\\\"180\\\"\\n              >\\n              <template slot-scope=\\\"scope\\\">\\n                <el-button size=\\\"mini\\\" type=\\\"success\\\" @click=\\\"updateData(scope.row.id)\\\">修改</el-button>\\n                <el-popconfirm\\n                  style=\\\"margin-left:5px\\\"\\n                  conf