INSERT INTO `ape_operate_log` VALUES ('1747857911409627137', '分页获取课程', 7, 'com.ape.apeadmin.controller.task.ApeTaskController.getApeTaskPage()', 'POST', 1, 'c864ceb6e258c75d183ba54a2033bd77', '白易', 'baiyi', '2024-01-18 13:46:14', '/task/getApeTaskPage', '127.0.0.1', NULL, '{}', '{\"code\":1000,\"data\":{\"current\":2,\"hitCount\":false,\"optimizeCountSql\":true,\"orders\":[],\"pages\":3,\"records\":[{\"classification\":\"Redis\",\"createBy\":\"超级管理员\",\"createTime\":\"2023-11-29 15:56:12\",\"id\":\"1729771224024518658\",\"image\":\"/img/1701244557328.jpeg\",\"major\":\"大数据\",\"name\":\"Redis基础入门\",\"num\":0,\"proportion\":70,\"remark\":\"\",\"state\":0,\"taskDescribe\":\"Redis（Remote Dictionary Server )，即远程字典服务，是一个开源的使用ANSI C语言编写、支持网络、可基于内存亦可持久化的日志型、Key-Value数据库，并提供多种语言的API。\",\"teacherId\":\"c864ceb6e258c75d183ba54a2033bd77\",\"teacherName\":\"白易\",\"updateBy\":\"超级管理员\",\"updateTime\":\"2023-11-29 16:13:18\"},{\"classification\":\"SpringCloud\",\"createBy\":\"超级管理员\",\"createTime\":\"2023-11-29 15:56:52\",\"id\":\"1729771390957817857\",\"image\":\"/img/1701244593291.png\",\"major\":\"操作系统\",\"name\":\"SpringCloud深入浅出\",\"num\":0,\"proportion\":70,\"remark\":\"\",\"state\":0,\"taskDescribe\":\"Spring Cloud是一系列框架的有序集合。它利用Spring Boot的开发便利性巧妙地简化了分布式系统基础设施的开发，如服务发现注册、配置中心、消息总线、负载均衡、断路器、数据监控等，都可以用Spring Boot的开发风格做到一键启动和部署。\",\"teacherId\":\"c864ceb6e258c75d183ba54a2033bd77\",\"teacherName\":\"白易\",\"updateBy\":\"超级管理员\",\"updateTime\":\"2023-11-29 16:13:19\"},{\"classification\":\"Shiro\",\"createBy\":\"超级管理员\",\"createTime\":\"2023-11-29 15:57:34\",\"id\":\"1729771566644629506\",\"image\":\"/img/1701244633306.png\",\"major\":\"计算机组成原理\",\"name\":\"Shiro权限框架\",\"num\":0,\"proportion\":70,\"remark\":\"\",\"state\":0,\"taskDescribe\":\"Apache Shiro是一个强大且易用的Java安全框架,执行身份验证、授权、密码和会话管理。使用Shiro的易于理解的API,您可以快速、轻松地获得任何应用程序,从最小的移动应用程序到最大的网络和企业应用程序。\",\"teacherId\":\"c864ceb6e258c75d183ba54a2033bd77\",\"teacherName\":\"白易\",\"updateBy\":\"超级管理员\",\"updateTime\":\"2023-11-29 16:13:19\"},{\"classification\":\"MongoDb\",\"createBy\":\"超级管理员\",\"createTime\":\"2023-11-29 15:58:18\",\"id\":\"1729771751051399170\",\"image\":\"/img/1701244674777.jpg\",\"major\":\"电子信息工程\",\"name\":\"MongoDb快速上手和集群搭建\",\"num\":0,\"proportion\":70,\"remark\":\"\",\"state\":0,\"taskDescribe\":\"MongoDB是一个基于分布式文件存储的数据库。由C++语言编写。旨在为WEB应用提供可扩展的高性能数据存储解决方案。MongoDB是一个介于关系数据库和非关系数据库之间的产品，是非关系数据库当中功能最丰富，最像关系数据库的。\",\"teacherId\":\"c864ceb6e258c75d183ba54a2033bd77\",\"teacherName\":\"白易\",\"updateBy\":\"超级管理员\",\"updateTime\":\"2023-11-29 16:13:20\"},{\"classification\":\"RabbitMq\",\"createBy\":\"超级管理员\",\"createTime\":\"2023-11-29 15:59:09\",\"id\":\"1729771964436615169\",\"image\":\"/img/1701244716609.jpeg\",\"major\":\"电子信息通信\",\"name\":\"RabbitMq教程（消息的发布与订阅）\",\"num\":0,\"proportion\":70,\"remark\":\"\",\"state\":0,\"taskDescribe\":\"RabbitMQ是实现了高级消息队列协议（AMQP）的开源消息代理软件（亦称面向消息的中间件）。RabbitMQ服务器是用Erlang语言编写的，而集群和故障转移是构建在开放电信平台框架上的。所有主要的编程语言均有与代理接口通讯的客户端库。\",\"teacherId\":\"c864ceb6e258c75d183ba54a2033bd77\",\"teacherName\":\"白易\",\"updateBy\":\"超级管理员\",\"updateTime\":\"2023-11-29 16:13:21\"},{\"classification\":\"ElasticSearch\",\"createBy\":\"超级管理员\",\"createTime\":\"2023-11-29 16:00:01\",\"id\":\"1729772184352362498\",\"image\":\"/img/1701244771002.jpg\",\"major\":\"大数据分析与治理\",\"name\":\"ElasticSearch快速上手实战教程\",\"num\":0,\"proportion\":70,\"remark\":\"\",\"state\":0,\"taskDescribe\":\"Elasticsearch 是位于 Elastic Stack 核心的分布式搜索和分析引擎。Logstash 和 Beats 有助于收集、聚合和丰富您的数据并将其存储在 Elasticsearch 中。Kibana 使您能够以交互方式探索、可视化和分享对数据的见解，并管理和监控堆栈。\",\"teacherId\":\"c864ceb6e258c75d183ba54a2033bd77\",\"teacherName\":\"白易\",\"updateBy\":\"超级管理员\",\"updateTime\":\"2023-11-29 16:13:22\"},{\"classification\":\"Jquery\",\"createBy\":\"超级管理员\",\"createTime\":\"2023-11-29 16:00:54\",\"id\":\"1729772407661301762\",\"image\":\"/img/1701244826923.png\",\"major\":\"软件工程\",\"name\":\"Jquery前端经典教程\",\"num\":0,\"proportion\":70,\"remark\":\"\",\"state\":0,\"taskDescribe\":\"jQuery是一个快速、简洁的JavaScript框架，是继Prototype之后又一个优秀的JavaScript代码库（框架）于2006年1月由John Resig发布。jQuery设计的宗旨是“write Less，Do More”，即倡导写更少的代码，做更多的事情。\",\"teacherId\":\"c864ceb6e258c75d183ba54a2033bd77\",\"teacherName\":\"白易\",\"updateBy\":\"超级管理员\",\"updateTime\":\"2023-11-29 16:13:23\"},{\"classification\":\"SpringSecurity\",\"createBy\":\"超级管理员\",\"createTime\":\"2023-11-29 16:01:59\",\"id\":\"1729772680341393409\",\"image\":\"/img/1701244887187.jpg\",\"major\":\"软件工程\",\"name\":\"SpringSecurity十小时入门到精通\",\"num\":0,\"proportion\":70,\"remark\":\"\",\"state\":0,\"taskDescribe\":\"Spring Security是一个能够为基于Spring的企业应用系统提供声明式的安全访问控制解决方案的安全框架。它提供了一组可以在Spring应用上下文中配置的Bean，充分利用了Spring IoC，DI（控制反转Inversion of Control ,DI:Dependency Injection 依赖注入）和AOP\",\"teacherId\":\"c864ceb6e258c75d183ba54a2033bd77\",\"teacherName\":\"白易\",\"updateBy\":\"超级管理员\",\"updateTime\":\"2023-11-29 16:13:24\"},{\"classification\":\"Docker\",\"createBy\":\"超级管理员\",\"createTime\":\"2023-11-29 16:03:00\",\"id\":\"1729772935866781698\",\"image\":\"/img/1701244944951.jpg\",\"major\":\"软件工程\",\"name\":\"Docker企业级快速入门到生产案例应用实践\",\"num\":0,\"proportion\":70,\"remark\":\"\",\"state\":0,\"taskDescribe\":\"Docker 是一个开源的应用容器引擎，让开发者可以打包他们的应用以及依赖包到一个可移植的镜像中，然后发布到任何流行的 Linux或Windows操作系统的机器上，也可以实现虚拟化。容器是完全使用沙箱机制，相互之间不会有任何接口。\",\"teacherId\":\"c864ceb6e258c75d183ba54a2033bd77\",\"teacherName\":\"白易\",\"updateBy\":\"超级管理员\",\"updateTime\":\"2023-11-29 16:13:25\"},{\"classification\":\"Linux\",\"createBy\":\"超级管理员\",\"createTime\":\"2023-11-29 16:10:35\",\"id\":\"1729774841670438914\",\"image\":\"/img/1701245411422.jpg\",\"major\":\"软件工程\",\"name\":\"Linux底层原理\",\"num\":0,\"proportion\":70,\"remark\":\"\",\"state\":0,\"taskDescribe\":\"Linux，一般指GNU/Linux（单独的Linux内核并不可直接使用，一般搭配GNU套件，故得此称呼），是一种免费使用和自由传播的类UNIX操作系统，其内核由林纳斯·本纳第克特·托瓦兹（Linus Benedict Torvalds）于1991年10月5日首次发布\",\"teacherId\":\"c864ceb6e258c75d183ba54a2033bd77\",\"teacherName\":\"白易\",\"updateBy\":\"超级管理员\",\"updateTime\":\"2023-11-29 16:13:26\"}],\"searchCount\":true,\"size\":10,\"total\":23},\"message\":\"操作成功!\",\"success\":true}', 0, NULL);
