INSERT INTO `ape_operate_log` VALUES ('1747858564785758209', '分页获取笔记收藏', 7, 'com.ape.apeadmin.controller.article.ApeArticleFavorController.getApeArticleFavorPage()', 'POST', 1, '769dc998d617bff4e30c4bde94f37e90', '文西', 'wenxi', '2024-01-18 13:48:50', '/favor/getApeArticleFavorPage', '127.0.0.1', NULL, '{}', '{\"code\":1000,\"data\":{\"current\":1,\"hitCount\":false,\"optimizeCountSql\":true,\"orders\":[],\"pages\":1,\"records\":[{\"articleDesc\":\"uniapp：九宫格，简单易上手，拿来即用\",\"articleId\":\"1730048774575534082\",\"avatar\":\"/img/1697436716646531073.jpg\",\"createBy\":\"白易\",\"createTime\":\"2023-11-30 10:19:05\",\"id\":\"1730228919086403586\",\"title\":\"uniapp：九宫格，简单易上手，拿来即用\",\"userId\":\"769dc998d617bff4e30c4bde94f37e90\"},{\"articleDesc\":\"uniapp开发多端应用，打包ios应用后，会生成一个ipa后缀的文件。这个文件无法直接安装在iphone上，需要将这个ipa文件上架app store后，才能通过app store来安装。\\n\\n但是苹果官方提供的上传工具xcode只能安装在mac电脑上，windows上并没有windows版的上传工具。因此我们这篇文章将教会大家如何通过windows电脑来上架。\",\"articleId\":\"1730049091539087362\",\"avatar\":\"/img/1697436716646531073.jpg\",\"createBy\":\"白易\",\"createTime\":\"2023-11-30 10:20:21\",\"id\":\"1730228935372886017\",\"title\":\"uniapp上架app store详细攻略\",\"userId\":\"769dc998d617bff4e30c4bde94f37e90\"},{\"articleDesc\":\"什么是线程（Thread）？进程和线程有什么区别？\",\"articleId\":\"1730049650568507394\",\"avatar\":\"/img/1697436716646531073.jpg\",\"createBy\":\"白易\",\"createTime\":\"2023-11-30 10:22:34\",\"id\":\"1730228950719844354\",\"title\":\"什么是线程（Thread）？进程和线程有什么区别？\",\"userId\":\"769dc998d617bff4e30c4bde94f37e90\"},{\"articleDesc\":\"一个线程就是一个 \\\"执行流\\\". 每个线程之间都可以按照顺序执行自己的代码. 多个线程之间 \\\"同时\\\" 执行着多份代码.\",\"articleId\":\"1730049947718168578\",\"avatar\":\"/img/1697436716646531073.jpg\",\"createBy\":\"白易\",\"createTime\":\"2023-11-30 10:23:45\",\"id\":\"1730228963302756354\",\"title\":\"【JavaEE初阶】认识线程、创建线程\",\"userId\":\"769dc998d617bff4e30c4bde94f37e90\"},{\"articleDesc\":\"动手前一定要调研项目中使用的组件，某些组件是没法升级的。比如ElasticSearch，驱动版本和ElasticSearch server版本要一致，升级了就会报错。其它常见组件的有Nacos，Kafka，Mysql，RocketMQ，需要调研是否兼容低版本。\\n如果之前使用了Zuul 1.x作为Gateway，Zuul 2.x不开源并且难以升级，建议升级到Spring Cloud Gateway\\n如果使用了Zuul1.x的作为Proxy嵌入服务中，有两个思路，一个是使用Filter+HttpClient手动写转发代码，第二个思路是调研调用接口，使用Feign做转发\\nSpring Boot 3.x 需要JDK17，建议使用OpenJDK17，Oracle的JDK17可能存在授权问题\",\"articleId\":\"1730051124686008322\",\"avatar\":\"/img/1697436716646531073.jpg\",\"createBy\":\"白易\",\"createTime\":\"2023-11-30 10:28:26\",\"id\":\"1730228987029934081\",\"title\":\"Spring Boot 升级3.x 指南\",\"userId\":\"769dc998d617bff4e30c4bde94f37e90\"},{\"articleDesc\":\"同步指的是任务按顺序依次执行，一个任务的执行必须等待前一个任务的完成。\\n在同步处理中，任务的调用者需要主动等待任务的完成，并在等待期间阻塞并占用资源。\\n在同步模式下，任务的执行顺序是确定的，具有可预测性。\",\"articleId\":\"1730064463969431553\",\"avatar\":\"/img/1697436716646531073.jpg\",\"createBy\":\"白易\",\"createTime\":\"2023-11-30 11:21:26\",\"id\":\"1730229048296132610\",\"title\":\"BIO、NIO和AIO的区别\",\"userId\":\"769dc998d617bff4e30c4bde94f37e90\"},{\"articleDesc\":\"实现将指定的多个文件打包成一个压缩文件下载。\",\"articleId\":\"1730047963766239234\",\"avatar\":\"/img/1697436716646531073.jpg\",\"createBy\":\"白易\",\"createTime\":\"2023-11-30 10:15:52\",\"id\":\"1731329118067073025\",\"title\":\"SpringBoot实现文件批量打包下载\",\"userId\":\"769dc998d617bff4e30c4bde94f37e90\"}],\"searchCount\":true,\"size\":10,\"total\":7},\"message\":\"操作成功!\",\"success\":true}', 0, NULL);
INSERT INTO `ape_operate_log` VALUES ('1747858845934149634', '分页获取我的课程', 7, 'com.ape.apeadmin.controller.task.ApeTaskStudentController.getApeMyTaskPage()', 'POST', 1, '769dc998d617bff4e30c4bde94f37e90', '文西', 'wenxi', '2024-01-18 13:49:57', '/student/getApeMyTaskPage', '127.0.0.1', NULL, '{}', '{\"code\":1000,\"data\":{\"current\":1,\"hitCount\":false,\"optimizeCountSql\":true,\"orders\":[],\"pages\":1,\"records\":[{\"id\":\"1730038492058910721\",\"image\":\"/img/1701239807968.jpg\",\"num\":3,\"state\":0,\"taskDescribe\":\"本书是针对Java语言初学者编写的基础教程，包含Java程序设计的基础知识，以及大量实用性很强的编程实例。全书共分11章，包括Java语言概述、Java语言基础、类与对象、继承与接口、数组与字符串、Java的异常处理机制、Java常见类库、输入输出及数据库操作、多线程、Applet程序及应用和图形用户界面设计。\",\"taskId\":\"1729751427215790082\",\"taskName\":\"Java零基础入门\",\"teacherId\":\"c864ceb6e258c75d183ba54a2033bd77\",\"teacherName\":\"白易\",\"userId\":\"769dc998d617bff4e30c4bde94f37e90\",\"userName\":\"文西\"},{\"id\":\"1747857857483460610\",\"image\":\"/img/1701243625276.jpg\",\"num\":2,\"state\":0,\"taskDescribe\":\"Mybatis是一款优秀的持久层框架,支持定制化 SQL、存储过程以及高级映射的优秀的持久层框架。MyBatis 避免了几乎所有的 JDBC 代码和手动设置参数以及获取结果集。MyBatis 可以对配置和原生Map使用简单的 XML 或注解,将接口和 Java 的 POJOs(Plain Old Java Objects,普通的 Java对象)映射成数据库中的记录。\",\"taskId\":\"1729767297673674754\",\"taskName\":\"Mybatis详细教程入门\",\"teacherId\":\"c864ceb6e258c75d183ba54a2033bd77\",\"teacherName\":\"白易\",\"userId\":\"769dc998d617bff4e30c4bde94f37e90\",\"userName\":\"文西\"}],\"searchCount\":true,\"size\":12,\"total\":2},\"message\":\"操作成功!\",\"success\":true}', 0, NULL);
